/*! @brief LARTGS generates a plane rotation designed to introduce a bulge \n
     in implicit QR iteration for the bidiagonal SVD problem
 * @details
 * \b Purpose:
    \verbatim
     LARTGS generates a plane rotation designed to introduce a bulge in
     Golub-Reinsch-style implicit QR iteration for the bidiagonal SVD
     problem. X and Y are the top-row entries, and SIGMA is the shift.
     The computed CS and SN define a plane rotation satisfying

        [  CS  SN  ]  .  [ X^2 - SIGMA ]  =  [ R ],
        [ -SN  CS  ]     [    X * Y    ]     [ 0 ]

     with R nonnegative.  If X^2 - SIGMA and X * Y are 0, then the
     rotation is by PI/2.
    \endverbatim 

 * @param[in] X
 
          X is REAL
          The (1,1) entry of an upper bidiagonal matrix.
 

 * @param[in] Y
 
          Y is REAL
          The (1,2) entry of an upper bidiagonal matrix.
 

 * @param[in] SIGMA
 
          SIGMA is REAL
          The shift.
 

 * @param[out] CS
 
          CS is REAL
          The cosine of the rotation.
 

 * @param[out] SN
 
          SN is REAL
          The sine of the rotation.

 * @return INTEGER Return value of the function.
 * */
template< typename T >
integer lartgs(T* x, T* y, T* sigma, T* cs, T* sn)
{
  return lartgs(x, y, sigma, cs, sn);
}

/*! @brief LASCL multiplies a general rectangular matrix by a real scalar defined as cto/cfrom

 * @details
 * \b Purpose:
    \verbatim
     LASCL multiplies the M by N real matrix A by the real scalar
     CTO/CFROM.  This is done without over/underflow as long as the final
     result CTO*A(I,J)/CFROM does not over/underflow. TYPE specifies that
     A may be full, upper triangular, lower triangular, upper Hessenberg,
     or banded.
    \endverbatim 

 * @param[in] TYPE
 
          TYPE is CHARACTER*1
          TYPE indices the storage type of the input matrix.
          = 'G':  A is a full matrix.
          = 'L':  A is a lower triangular matrix.
          = 'U':  A is an upper triangular matrix.
          = 'H':  A is an upper Hessenberg matrix.
          = 'B':  A is a symmetric band matrix with lower bandwidth KL
                  and upper bandwidth KU and with the only the lower
                  half stored.
          = 'Q':  A is a symmetric band matrix with lower bandwidth KL
                  and upper bandwidth KU and with the only the upper
                  half stored.
          = 'Z':  A is a band matrix with lower bandwidth KL and upper
                  bandwidth KU. See SGBTRF for storage details.
 

 * @param[in] KL
 
          KL is INTEGER
          The lower bandwidth of A.  Referenced only if TYPE = 'B',
          'Q' or 'Z'.
 

 * @param[in] KU
 
          KU is INTEGER
          The upper bandwidth of A.  Referenced only if TYPE = 'B',
          'Q' or 'Z'.
 

 * @param[in] CFROM
 
          CFROM is REAL
 

 * @param[in] CTO
 
          CTO is REAL

          The matrix A is multiplied by CTO/CFROM. A(I,J) is computed
          without over/underflow if the final result CTO*A(I,J)/CFROM
          can be represented without over/underflow.  CFROM must be
          nonzero.
 

 * @param[in] M
 
          M is INTEGER
          The number of rows of the matrix A.  M >= 0.
 

 * @param[in] N
 
          N is INTEGER
          The number of columns of the matrix A.  N >= 0.
 

 * @param[in,out] A
 
          A is REAL array, dimension (LDA,N)
          The matrix to be multiplied by CTO/CFROM.  See TYPE for the
          storage type.
 

 * @param[in] LDA
 
          LDA is INTEGER
          The leading dimension of the array A.
          If TYPE = 'G', 'L', 'U', 'H', LDA >= max(1,M);
             TYPE = 'B', LDA >= KL+1;
             TYPE = 'Q', LDA >= KU+1;
             TYPE = 'Z', LDA >= 2*KL+KU+1.
 * @param[out]	INFO	
          INFO is INTEGER
          0  - successful exit
          <0 - if INFO = -i, the i-th argument had an illegal value.

 * @return INTEGER Return value of the function.
 * */
template< typename T >
integer lascl(char* type, integer* kl, integer* ku, T* cfrom, T* cto, integer* m, integer* n, T* a, integer* lda, integer* info)
{
  return lascl(type, kl, ku, cfrom, cto, m, n, a, lda, info);
}
template< typename T , typename Ta >
integer lascl(char* type, integer* kl, integer* ku, Ta* cfrom, Ta* cto, integer* m, integer* n, T* a, integer* lda, integer* info)
{
  return lascl(type, kl, ku, cfrom, cto, m, n, a, lda, info);
}

/*! @brief LASRT sorts numbers in increasing or decreasing order

 * @details
 * \b Purpose:
    \verbatim
     Sort the numbers in D in increasing order (if ID = 'I') or
     in decreasing order (if ID = 'D').

     Use Quick Sort, reverting to Insertion sort on arrays of
     size <= 20. Dimension of STACK limits N to about 2**32.
    \verbatim 

 * @param[in] ID
 
          ID is CHARACTER*1
          = 'I': sort D in increasing order;
          = 'D': sort D in decreasing order.
 

 * @param[in] N
 
          N is INTEGER
          The length of the array D.
 

 * @param[in,out] D
 
          D is REAL array, dimension (N)
          On entry, the array to be sorted.
          On exit, D has been sorted into increasing order
          (D(1) <= ... <= D(N)) or into decreasing order
          (D(1) >= ... >= D(N)), depending on ID.
 * @param[out]	INFO	
          INFO is INTEGER
          = 0:  successful exit
          < 0:  if INFO = -i, the i-th argument had an illegal value

 * @return INTEGER Return value of the function.
 * */
template< typename T >
integer lasrt(char* id, integer* n, T* d, integer* info)
{
  return lasrt(id, n, d, info);
}

/*! @brief LASSQ updates a sum of squares represented in scaled form

 * @details
 * \b Purpose:
    \verbatim
     LASSQ  returns the values  scl  and  smsq  such that

        ( scl**2)*smsq = x( 1)**2 +...+ x( n)**2 + ( scale**2)*sumsq,

     where  x( i) = X( 1 + ( i - 1)*INCX). The value of  sumsq  is
     assumed to be non-negative and  scl  returns the value

        scl = max( scale, abs( x( i))).

     scale and sumsq must be supplied in SCALE and SUMSQ and
     scl and smsq are overwritten on SCALE and SUMSQ respectively.

     The routine makes only one pass through the vector x.
    \endverbatim 

 * @param[in] N
 
          N is INTEGER
          The number of elements to be used from the vector X.
 

 * @param[in] X
 
          X is REAL array, dimension (1+(N-1)*INCX)
          The vector for which a scaled sum of squares is computed.
             x( i)  = X( 1 + ( i - 1)*INCX), 1 <= i <= n.
 

 * @param[in] INCX
 
          INCX is INTEGER
          The increment between successive values of the vector X.
          INCX > 0.
 

 * @param[in,out] SCALE
 
          SCALE is REAL
          On entry, the value  scale  in the equation above.
          On exit, SCALE is overwritten with  scl , the scaling factor
          for the sum of squares.
 

 * @param[in,out] SUMSQ
 
          SUMSQ is REAL
          On entry, the value  sumsq  in the equation above.
          On exit, SUMSQ is overwritten with  smsq , the basic sum of
          squares from which  scl  has been factored out.

 * @return INTEGER Return value of the function.
 * */
template< typename T >
integer lassq(integer* n, T* x, integer* incx, T* scale, T* sumsq )
{
  return lassq(n, x, incx, scale, sumsq);
}
template< typename T, typename Ta >
integer lassq(integer* n, T* x, integer* incx, Ta* scale, Ta* sumsq )
{
  return lassq( n, x, incx, scale, sumsq);
}

/*! @brief OPGTR generates a real orthogonal matrix Q

 * @details
 * \b Purpose:
    \verbatim 
     OPGTR generates a real orthogonal matrix Q which is defined as the
     product of n-1 elementary reflectors H(i) of order n, as returned by
     SSPTRD using packed storage:

     if UPLO = 'U', Q = H(n-1) . . . H(2) H(1),

     if UPLO = 'L', Q = H(1) H(2) . . . H(n-1).
    \endverbatim  

 * @param[in] UPLO
 
          UPLO is CHARACTER*1
          = 'U': Upper triangular packed storage used in previous
                 call to SSPTRD;
          = 'L': Lower triangular packed storage used in previous
                 call to SSPTRD.
 

 * @param[in] N
 
          N is INTEGER
          The order of the matrix Q. N >= 0.
 

 * @param[in] AP
 
          AP is REAL array, dimension (N*(N+1)/2)
          The vectors which define the elementary reflectors, as
          returned by SSPTRD.
 

 * @param[in] TAU
 
          TAU is REAL array, dimension (N-1)
          TAU(i) must contain the scalar factor of the elementary
          reflector H(i), as returned by SSPTRD.
 

 * @param[out] Q
 
          Q is REAL array, dimension (LDQ,N)
          The N-by-N orthogonal matrix Q.
 

 * @param[in] LDQ
 
          LDQ is INTEGER
          The leading dimension of the array Q. LDQ >= max(1,N).
 * @param[out]	WORK	
          WORK is REAL array, dimension (N-1)
 * @param[out]	INFO	
          INFO is INTEGER
          = 0:  successful exit
          < 0:  if INFO = -i, the i-th argument had an illegal value

 * @return INTEGER Return value of the function.
 * */
template< typename T >
integer opgtr(char* uplo, integer* n,  T* ap,  T* tau, T* q, integer* ldq, T *work, integer *info)
{
  return opgtr(uplo, n, ap, tau, q, ldq, work, info);
}
template< typename T >
integer upgtr(char* uplo, integer* n,  T* ap,  T* tau, T* q, integer* ldq, T *work, integer *info)
{
  return upgtr(uplo, n, ap, tau, q, ldq, work, info);
}

/*! @brief OPMTR overwrites the general real M-by-N matrix

 * @details
 * \b Purpose:
    \verbatim
     OPMTR overwrites the general real M-by-N matrix C with
                     SIDE = 'L'     SIDE = 'R'
     TRANS = 'N':      Q * C          C * Q
     TRANS = 'T':      Q**T * C       C * Q**T

     where Q is a real orthogonal matrix of order nq, with nq = m if
     SIDE = 'L' and nq = n if SIDE = 'R'. Q is defined as the product of
     nq-1 elementary reflectors, as returned by SSPTRD using packed
     storage:
     if UPLO = 'U', Q = H(nq-1) . . . H(2) H(1);
     if UPLO = 'L', Q = H(1) H(2) . . . H(nq-1).
    \endverbatim 

 * @param[in] SIDE
 
          SIDE is CHARACTER*1
          = 'L': apply Q or Q**T from the Left;
          = 'R': apply Q or Q**T from the Right.
 

 * @param[in] UPLO
 
          UPLO is CHARACTER*1
          = 'U': Upper triangular packed storage used in previous
                 call to SSPTRD;
          = 'L': Lower triangular packed storage used in previous
                 call to SSPTRD.
 

 * @param[in] TRANS
 
          TRANS is CHARACTER*1
          = 'N':  No transpose, apply Q;
          = 'T':  Transpose, apply Q**T.
 

 * @param[in] M
 
          M is INTEGER
          The number of rows of the matrix C. M >= 0.
 

 * @param[in] N
 
          N is INTEGER
          The number of columns of the matrix C. N >= 0.
 

 * @param[in] AP
 
          AP is REAL array, dimension
                               (M*(M+1)/2) if SIDE = 'L'
                               (N*(N+1)/2) if SIDE = 'R'
          The vectors which define the elementary reflectors, as
          returned by SSPTRD.  AP is modified by the routine but
          restored on exit.
 

 * @param[in] TAU
 
          TAU is REAL array, dimension (M-1) if SIDE = 'L'
                                     or (N-1) if SIDE = 'R'
          TAU(i) must contain the scalar factor of the elementary
          reflector H(i), as returned by SSPTRD.
 

 * @param[in,out] C
 
          C is REAL array, dimension (LDC,N)
          On entry, the M-by-N matrix C.
          On exit, C is overwritten by Q*C or Q**T*C or C*Q**T or C*Q.
 

 * @param[in] LDC
 
          LDC is INTEGER
          The leading dimension of the array C. LDC >= max(1,M).
 * @param[out]	WORK	
          WORK is REAL array, dimension
                                   (N) if SIDE = 'L'
                                   (M) if SIDE = 'R'
 * @param[out]	INFO	
          INFO is INTEGER
          = 0:  successful exit
          < 0:  if INFO = -i, the i-th argument had an illegal value

 * @return INTEGER Return value of the function.
 * */
template< typename T >
integer opmtr(char* side, char* uplo, char* trans, integer* m, integer* n,  T* ap,  T* tau, T* c, integer* ldc, T* work, integer* info)
{
  return opmtr(side, uplo, trans, m, n,  ap,  tau, c, ldc, work, info);
}

template< typename T >
integer upmtr(char* side, char* uplo, char* trans, integer* m, integer* n,  T* ap,  T* tau, T* c, integer* ldc, T* work, integer* info)
{
  return upmtr(side, uplo, trans, m, n,  ap,  tau, c, ldc, work, info);
}

/*! @brief ORBDB simultaneously bidiagonalizes the blocks of an M-by-M \n
 partitioned orthogonal matrix X
 * @details
 * \b Purpose:
    \verbatim
     ORBDB simultaneously bidiagonalizes the blocks of an M-by-M
     partitioned orthogonal matrix X:

                                     [ B11 | B12 0  0 ]
         [ X11 | X12 ]   [ P1 |    ] [  0  |  0 -I  0 ] [ Q1 |    ]**T
     X = [-----------] = [---------] [----------------] [---------]   .
         [ X21 | X22 ]   [    | P2 ] [ B21 | B22 0  0 ] [    | Q2 ]
                                     [  0  |  0  0  I ]

     X11 is P-by-Q. Q must be no larger than P, M-P, or M-Q. (If this is
     not the case, then X must be transposed and/or permuted. This can be
     done in constant time using the TRANS and SIGNS options. See SORCSD
     for details.)

     The orthogonal matrices P1, P2, Q1, and Q2 are P-by-P, (M-P)-by-
     (M-P), Q-by-Q, and (M-Q)-by-(M-Q), respectively. They are
     represented implicitly by Householder vectors.

     B11, B12, B21, and B22 are Q-by-Q bidiagonal matrices represented
     implicitly by angles THETA, PHI.
    \endverbatim 

 * @param[in] TRANS
 
          TRANS is CHARACTER
          = 'T':      X, U1, U2, V1T, and V2T are stored in row-major
                      order;
          otherwise:  X, U1, U2, V1T, and V2T are stored in column-
                      major order.
 

 * @param[in] SIGNS
 
          SIGNS is CHARACTER
          = 'O':      The lower-left block is made nonpositive (the
                      "other" convention);
          otherwise:  The upper-right block is made nonpositive (the
                      "default" convention).
 

 * @param[in] M
 
          M is INTEGER
          The number of rows and columns in X.
 

 * @param[in] P
 
          P is INTEGER
          The number of rows in X11 and X12. 0 <= P <= M.
 

 * @param[in] Q
 
          Q is INTEGER
          The number of columns in X11 and X21. 0 <= Q <=
          MIN(P,M-P,M-Q).
 

 * @param[in,out] X11
 
          X11 is REAL array, dimension (LDX11,Q)
          On entry, the top-left block of the orthogonal matrix to be
          reduced. On exit, the form depends on TRANS:
          If TRANS = 'N', then
             the columns of tril(X11) specify reflectors for P1,
             the rows of triu(X11,1) specify reflectors for Q1;
          else TRANS = 'T', and
             the rows of triu(X11) specify reflectors for P1,
             the columns of tril(X11,-1) specify reflectors for Q1.
 

 * @param[in] LDX11
 
          LDX11 is INTEGER
          The leading dimension of X11. If TRANS = 'N', then LDX11 >=
          P; else LDX11 >= Q.
 

 * @param[in,out] X12
 
          X12 is REAL array, dimension (LDX12,M-Q)
          On entry, the top-right block of the orthogonal matrix to
          be reduced. On exit, the form depends on TRANS:
          If TRANS = 'N', then
             the rows of triu(X12) specify the first P reflectors for
             Q2;
          else TRANS = 'T', and
             the columns of tril(X12) specify the first P reflectors
             for Q2.
 

 * @param[in] LDX12
 
          LDX12 is INTEGER
          The leading dimension of X12. If TRANS = 'N', then LDX12 >=
          P; else LDX11 >= M-Q.
 

 * @param[in,out] X21
 
          X21 is REAL array, dimension (LDX21,Q)
          On entry, the bottom-left block of the orthogonal matrix to
          be reduced. On exit, the form depends on TRANS:
          If TRANS = 'N', then
             the columns of tril(X21) specify reflectors for P2;
          else TRANS = 'T', and
             the rows of triu(X21) specify reflectors for P2.
 

 * @param[in] LDX21
 
          LDX21 is INTEGER
          The leading dimension of X21. If TRANS = 'N', then LDX21 >=
          M-P; else LDX21 >= Q.
 

 * @param[in,out] X22
 
          X22 is REAL array, dimension (LDX22,M-Q)
          On entry, the bottom-right block of the orthogonal matrix to
          be reduced. On exit, the form depends on TRANS:
          If TRANS = 'N', then
             the rows of triu(X22(Q+1:M-P,P+1:M-Q)) specify the last
             M-P-Q reflectors for Q2,
          else TRANS = 'T', and
             the columns of tril(X22(P+1:M-Q,Q+1:M-P)) specify the last
             M-P-Q reflectors for P2.
 

 * @param[in] LDX22
 
          LDX22 is INTEGER
          The leading dimension of X22. If TRANS = 'N', then LDX22 >=
          M-P; else LDX22 >= M-Q.
 

 * @param[out] THETA
 
          THETA is REAL array, dimension (Q)
          The entries of the bidiagonal blocks B11, B12, B21, B22 can
          be computed from the angles THETA and PHI. See Further
          Details.
 

 * @param[out] PHI
 
          PHI is REAL array, dimension (Q-1)
          The entries of the bidiagonal blocks B11, B12, B21, B22 can
          be computed from the angles THETA and PHI. See Further
          Details.
 

 * @param[out] TAUP1
 
          TAUP1 is REAL array, dimension (P)
          The scalar factors of the elementary reflectors that define
          P1.
 

 * @param[out] TAUP2
 
          TAUP2 is REAL array, dimension (M-P)
          The scalar factors of the elementary reflectors that define
          P2.
 

 * @param[out] TAUQ1
 
          TAUQ1 is REAL array, dimension (Q)
          The scalar factors of the elementary reflectors that define
          Q1.
 

 * @param[out] TAUQ2
 
          TAUQ2 is REAL array, dimension (M-Q)
          The scalar factors of the elementary reflectors that define
          Q2.
 * @param[out]	WORK	
          WORK is REAL array, dimension (LWORK)
 * @param[in]	LWORK	
          LWORK is INTEGER
          The dimension of the array WORK. LWORK >= M-Q.

          If LWORK = -1, then a workspace query is assumed; the routine
          only calculates the optimal size of the WORK array, returns
          this value as the first entry of the WORK array, and no error
          message related to LWORK is issued by XERBLA.
 * @param[out]	INFO	
          INFO is INTEGER
          = 0:  successful exit.
          < 0:  if INFO = -i, the i-th argument had an illegal value.


 * @return INTEGER Return value of the function.
 * */ 
template< typename T >
integer orbdb(char* trans, char* signs, integer* m, integer* p, integer* q, T* x11, integer* ldx11, T* x12, integer* ldx12, T* x21, integer* ldx21, T* x22, integer* ldx22, T* theta, T* phi, T* taup1, T* taup2, T* tauq1, T* tauq2, T* work, integer* lwork, integer* info)
{
  return orbdb(trans, signs, m, p, q, x11, ldx11, x12, ldx12, x21, ldx21, x22, ldx22, theta, phi, taup1, taup2, tauq1, tauq2, work, lwork, info);
}

template< typename T, typename Ta >
integer unbdb(char* trans, char* signs, integer* m, integer* p, integer* q, T* x11, integer* ldx11, T* x12, integer* ldx12, T* x21, integer* ldx21, T* x22, integer* ldx22, Ta* theta, Ta* phi, T* taup1, T* taup2, T* tauq1, T* tauq2, T* work, integer* lwork, integer* info)
{
  return unbdb(trans, signs, m, p, q, x11, ldx11, x12, ldx12, x21, ldx21, x22, ldx22, theta, phi, taup1, taup2, tauq1, tauq2, work, lwork, info);
}

/*! @brief ORCSD computes the CS decomposition of an M-by-M partitioned orthogonal/unitary matrix X

 * @details
 * \b Purpose:
    \verbatim 
     ORCSD computes the CS decomposition of an M-by-M partitioned
     orthogonal/unitary  matrix X:

                                     [  I  0  0 |  0  0  0 ]
                                     [  0  C  0 |  0 -S  0 ]
         [ X11 | X12 ]   [ U1 |    ] [  0  0  0 |  0  0 -I ] [ V1 |    ]**T
     X = [-----------] = [---------] [---------------------] [---------]   .
         [ X21 | X22 ]   [    | U2 ] [  0  0  0 |  I  0  0 ] [    | V2 ]
                                     [  0  S  0 |  0  C  0 ]
                                     [  0  0  I |  0  0  0 ]

     X11 is P-by-Q. The orthogonal/unitary  matrices U1, U2, V1, and V2 are P-by-P,
     (M-P)-by-(M-P), Q-by-Q, and (M-Q)-by-(M-Q), respectively. C and S are
     R-by-R nonnegative diagonal matrices satisfying C^2 + S^2 = I, in
     which R = MIN(P,M-P,Q,M-Q).
    \endverbatim 

 * @param[in] JOBU1
 
          JOBU1 is CHARACTER
          = 'Y':      U1 is computed;
          otherwise:  U1 is not computed.
 

 * @param[in] JOBU2
 
          JOBU2 is CHARACTER
          = 'Y':      U2 is computed;
          otherwise:  U2 is not computed.
 

 * @param[in] JOBV1T
 
          JOBV1T is CHARACTER
          = 'Y':      V1T is computed;
          otherwise:  V1T is not computed.
 

 * @param[in] JOBV2T
 
          JOBV2T is CHARACTER
          = 'Y':      V2T is computed;
          otherwise:  V2T is not computed.
 

 * @param[in] TRANS
 
          TRANS is CHARACTER
          = 'T':      X, U1, U2, V1T, and V2T are stored in row-major
                      order;
          otherwise:  X, U1, U2, V1T, and V2T are stored in column-
                      major order.
 

 * @param[in] SIGNS
 
          SIGNS is CHARACTER
          = 'O':      The lower-left block is made nonpositive (the
                      "other" convention);
          otherwise:  The upper-right block is made nonpositive (the
                      "default" convention).
 

 * @param[in] M
 
          M is INTEGER
          The number of rows and columns in X.
 

 * @param[in] P
 
          P is INTEGER
          The number of rows in X11 and X12. 0 <= P <= M.
 

 * @param[in] Q
 
          Q is INTEGER
          The number of columns in X11 and X21. 0 <= Q <= M.
 

 * @param[in,out] X11
 
          X11 is REAL array, dimension (LDX11,Q)
          On entry, part of the orthogonal matrix whose CSD is desired.
 

 * @param[in] LDX11
 
          LDX11 is INTEGER
          The leading dimension of X11. LDX11 >= MAX(1,P).
 

 * @param[in,out] X12
 
          X12 is REAL array, dimension (LDX12,M-Q)
          On entry, part of the orthogonal matrix whose CSD is desired.
 

 * @param[in] LDX12
 
          LDX12 is INTEGER
          The leading dimension of X12. LDX12 >= MAX(1,P).
 

 * @param[in,out] X21
 
          X21 is REAL array, dimension (LDX21,Q)
          On entry, part of the orthogonal matrix whose CSD is desired.
 

 * @param[in] LDX21
 
          LDX21 is INTEGER
          The leading dimension of X11. LDX21 >= MAX(1,M-P).
 

 * @param[in,out] X22
 
          X22 is REAL array, dimension (LDX22,M-Q)
          On entry, part of the orthogonal matrix whose CSD is desired.
 

 * @param[in] LDX22
 
          LDX22 is INTEGER
          The leading dimension of X11. LDX22 >= MAX(1,M-P).
 

 * @param[out] THETA
 
          THETA is REAL array, dimension (R), in which R =
          MIN(P,M-P,Q,M-Q).
          C = DIAG( COS(THETA(1)), ... , COS(THETA(R))) and
          S = DIAG( SIN(THETA(1)), ... , SIN(THETA(R))).
 

 * @param[out] U1
 
          U1 is REAL array, dimension (LDU1,P)
          If JOBU1 = 'Y', U1 contains the P-by-P orthogonal matrix U1.
 

 * @param[in] LDU1
 
          LDU1 is INTEGER
          The leading dimension of U1. If JOBU1 = 'Y', LDU1 >=
          MAX(1,P).
 

 * @param[out] U2
 
          U2 is REAL array, dimension (LDU2,M-P)
          If JOBU2 = 'Y', U2 contains the (M-P)-by-(M-P) orthogonal
          matrix U2.
 

 * @param[in] LDU2
 
          LDU2 is INTEGER
          The leading dimension of U2. If JOBU2 = 'Y', LDU2 >=
          MAX(1,M-P).
 

 * @param[out] V1T
 
          V1T is REAL array, dimension (LDV1T,Q)
          If JOBV1T = 'Y', V1T contains the Q-by-Q matrix orthogonal
          matrix V1**T.
 

 * @param[in] LDV1T
 
          LDV1T is INTEGER
          The leading dimension of V1T. If JOBV1T = 'Y', LDV1T >=
          MAX(1,Q).
 

 * @param[out] V2T
 
          V2T is REAL array, dimension (LDV2T,M-Q)
          If JOBV2T = 'Y', V2T contains the (M-Q)-by-(M-Q) orthogonal
          matrix V2**T.
 

 * @param[in] LDV2T
 
          LDV2T is INTEGER
          The leading dimension of V2T. If JOBV2T = 'Y', LDV2T >=
          MAX(1,M-Q).
 * @param[out]	WORK	
          WORK is REAL array, dimension (MAX(1,LWORK))
          On exit, if INFO = 0, WORK(1) returns the optimal LWORK.
          If INFO > 0 on exit, WORK(2:R) contains the values PHI(1),
          ..., PHI(R-1) that, together with THETA(1), ..., THETA(R),
          define the matrix in intermediate bidiagonal-block form
          remaining after nonconvergence. INFO specifies the number
          of nonzero PHI's.
 * @param[in]	LWORK	
          LWORK is INTEGER
          The dimension of the array WORK.

          If LWORK = -1, then a workspace query is assumed; the routine
          only calculates the optimal size of the WORK array, returns
          this value as the first entry of the work array, and no error
          message related to LWORK is issued by XERBLA.
 * @param[out]	IWORK	
          IWORK is INTEGER array, dimension (M-MIN(P, M-P, Q, M-Q))
 * @param[out]	INFO	
          INFO is INTEGER
          = 0:  successful exit.
          < 0:  if INFO = -i, the i-th argument had an illegal value.
          > 0:  SBBCSD did not converge. See the description of WORK
                above for details.

 * @return INTEGER Return value of the function.
 * */
template< typename T >
integer orcsd(char* jobu1, char* jobu2, char* jobv1t, char* jobv2t, char* trans, char* signs, integer* m, integer* p, integer* q, T* x11, integer* ldx11, T* x12, integer* ldx12, T* x21, integer* ldx21, T* x22, integer* ldx22, T* theta, T* u1, integer* ldu1, T* u2, integer* ldu2, T* v1t, integer* ldv1t, T* v2t, integer* ldv2t, T *work, integer *lwork, integer *iwork, integer *info)
{
  return orcsd(jobu1, jobu2, jobv1t, jobv2t, trans, signs, m, p, q, x11, ldx11, x12, ldx12, x21, ldx21, x22, ldx22, theta, u1, ldu1, u2, ldu2, v1t, ldv1t, v2t, ldv2t, work, lwork, iwork, info);
}
template< typename T, typename Ta >
integer uncsd(char* jobu1, char* jobu2, char* jobv1t, char* jobv2t, char* trans, char* signs, integer* m, integer* p, integer* q, T* x11, integer* ldx11, T* x12, integer* ldx12, T* x21, integer* ldx21, T* x22, integer* ldx22, Ta* theta, T* u1, integer* ldu1, T* u2, integer* ldu2, T* v1t, integer* ldv1t, T* v2t, integer* ldv2t, T* work, integer* lwork, Ta* rwork, integer* lrwork, integer* iwork, integer* info)
{
  return uncsd(jobu1, jobu2, jobv1t, jobv2t, trans, signs, m, p, q, x11, ldx11, x12, ldx12, x21, ldx21, x22, ldx22, theta, u1, ldu1, u2, ldu2, v1t, ldv1t, v2t, ldv2t, work, lwork, rwork, lrwork, iwork, info);
}

/*! @brief ORCSD2BY1 computes the CS decomposition of an M-by-Q matrix X with orthonormal columns

 * @details
 * \b Purpose:
    \verbatim 
     ORCSD2BY1 computes the CS decomposition of an M-by-Q matrix X with
     orthonormal columns that has been partitioned into a 2-by-1 block
     structure:

                                    [  I1 0  0 ]
                                    [  0  C  0 ]
              [ X11 ]   [ U1 |    ] [  0  0  0 ]
          X = [-----] = [---------] [----------] V1**T .
              [ X21 ]   [    | U2 ] [  0  0  0 ]
                                    [  0  S  0 ]
                                    [  0  0  I2]

     X11 is P-by-Q. The orthogonal matrices U1, U2, and V1 are P-by-P,
     (M-P)-by-(M-P), and Q-by-Q, respectively. C and S are R-by-R
     nonnegative diagonal matrices satisfying C^2 + S^2 = I, in which
     R = MIN(P,M-P,Q,M-Q). I1 is a K1-by-K1 identity matrix and I2 is a
     K2-by-K2 identity matrix, where K1 = MAX(Q+P-M,0), K2 = MAX(Q-P,0).
    \endverbatim  

 * @param[in] JOBU1
 
          JOBU1 is CHARACTER
          = 'Y':      U1 is computed;
          otherwise:  U1 is not computed.
 

 * @param[in] JOBU2
 
          JOBU2 is CHARACTER
          = 'Y':      U2 is computed;
          otherwise:  U2 is not computed.
 

 * @param[in] JOBV1T
 
          JOBV1T is CHARACTER
          = 'Y':      V1T is computed;
          otherwise:  V1T is not computed.
 

 * @param[in] M
 
          M is INTEGER
          The number of rows in X.
 

 * @param[in] P
 
          P is INTEGER
          The number of rows in X11. 0 <= P <= M.
 

 * @param[in] Q
 
          Q is INTEGER
          The number of columns in X11 and X21. 0 <= Q <= M.
 

 * @param[in,out] X11
 
          X11 is REAL array, dimension (LDX11,Q)
          On entry, part of the orthogonal matrix whose CSD is desired.
 

 * @param[in] LDX11
 
          LDX11 is INTEGER
          The leading dimension of X11. LDX11 >= MAX(1,P).
 

 * @param[in,out] X21
 
          X21 is REAL array, dimension (LDX21,Q)
          On entry, part of the orthogonal matrix whose CSD is desired.
 

 * @param[in] LDX21
 
          LDX21 is INTEGER
           The leading dimension of X21. LDX21 >= MAX(1,M-P).
 

 * @param[out] THETA
 
          THETA is REAL array, dimension (R), in which R =
          MIN(P,M-P,Q,M-Q).
          C = DIAG( COS(THETA(1)), ... , COS(THETA(R))) and
          S = DIAG( SIN(THETA(1)), ... , SIN(THETA(R))).
 

 * @param[out] U1
 
          U1 is REAL array, dimension (P)
          If JOBU1 = 'Y', U1 contains the P-by-P orthogonal matrix U1.
 

 * @param[in] LDU1
 
          LDU1 is INTEGER
          The leading dimension of U1. If JOBU1 = 'Y', LDU1 >=
          MAX(1,P).
 

 * @param[out] U2
 
          U2 is REAL array, dimension (M-P)
          If JOBU2 = 'Y', U2 contains the (M-P)-by-(M-P) orthogonal
          matrix U2.
 

 * @param[in] LDU2
 
          LDU2 is INTEGER
          The leading dimension of U2. If JOBU2 = 'Y', LDU2 >=
          MAX(1,M-P).
 

 * @param[out] V1T
 
          V1T is REAL array, dimension (Q)
          If JOBV1T = 'Y', V1T contains the Q-by-Q matrix orthogonal
          matrix V1**T.
 

 * @param[in] LDV1T
 
          LDV1T is INTEGER
          The leading dimension of V1T. If JOBV1T = 'Y', LDV1T >=
          MAX(1,Q).
 * @param[out]	WORK	
          WORK is REAL array, dimension (MAX(1,LWORK))
          On exit, if INFO = 0, WORK(1) returns the optimal LWORK.
          If INFO > 0 on exit, WORK(2:R) contains the values PHI(1),
          ..., PHI(R-1) that, together with THETA(1), ..., THETA(R),
          define the matrix in intermediate bidiagonal-block form
          remaining after nonconvergence. INFO specifies the number
          of nonzero PHI's.
 * @param[in]	LWORK	
          LWORK is INTEGER
          The dimension of the array WORK.

          If LWORK = -1, then a workspace query is assumed; the routine
          only calculates the optimal size of the WORK array, returns
          this value as the first entry of the work array, and no error
          message related to LWORK is issued by XERBLA.
 * @param[out]	IWORK	
          IWORK is INTEGER array, dimension (M-MIN(P,M-P,Q,M-Q))
 * @param[out]	INFO	
          INFO is INTEGER
          = 0:  successful exit.
          < 0:  if INFO = -i, the i-th argument had an illegal value.
          > 0:  SBBCSD did not converge. See the description of WORK
                above for details.

 * @return INTEGER Return value of the function.
 * */
template< typename T >
integer orcsd2by1(char* jobu1, char* jobu2, char* jobv1t, integer* m, integer* p, integer* q, T* x11, integer* ldx11, T* x21, integer* ldx21, T* theta, T* u1, integer* ldu1, T* u2, integer* ldu2, T* v1t, integer* ldv1t, T *work, integer *lwork, integer *iwork, integer *info)
{
  return orcsd2by1(jobu1, jobu2, jobv1t, m, p, q, x11, ldx11, x21, ldx21, theta, u1, ldu1, u2, ldu2, v1t, ldv1t, work, lwork, iwork, info);
}
