/*! @brief LARGV generates a vector of plane rotations with real cosines and real sines

 * @details
 * \b Purpose:
    \verbatim 
    LARGV generates a vector of real plane rotations, determined by
    elements of the real vectors x and y. For i = 1,2,...,n

       ( c(i)  s(i)) (x(i)) = (a(i))
       (-s(i)  c(i)) (y(i)) = (  0 )
    \endverbatim 


 * @param[in] N
 
          N is INTEGER
          The number of plane rotations to be generated.
 

 * @param[in,out] X
 
          X is REAL array,
                         dimension (1+(N-1)*INCX)
          On entry, the vector x.
          On exit, x(i) is overwritten by a(i), for i = 1,...,n.
 

 * @param[in] INCX
 
          INCX is INTEGER
          The increment between elements of X. INCX > 0.
 

 * @param[in,out] Y
 
          Y is REAL array,
                         dimension (1+(N-1)*INCY)
          On entry, the vector y.
          On exit, the sines of the plane rotations.
 

 * @param[in] INCY
 
          INCY is INTEGER
          The increment between elements of Y. INCY > 0.
 

 * @param[out] C
 
          C is REAL array, dimension (1+(N-1)*INCC)
          The cosines of the plane rotations.
 

 * @param[in] INCC
 
          INCC is INTEGER
          The increment between elements of C. INCC > 0.
 * */         
template< typename T >
integer largv(integer *n, T *x, integer *incx, T *y, integer *incy, T *c, integer *incc)
{
  return largv(n, x, incx, y, incy, c, incc);
}
template< typename T, typename Ta >
integer largv(integer *n, T *x, integer *incx, T *y, integer *incy, Ta *c, integer *incc)
{
  return largv(n, x, incx, y, incy, c, incc);
}

/*! @brief LARRA computes the splitting points with the specified threshold

 * @details
 * \b Purpose:
    \verbatim 
    
    Compute the splitting points with threshold SPLTOL.
    LARRA sets any "small" off-diagonal elements to zero.
 
    \endverbatim 

 * @param[in] N
 
          N is INTEGER
          The order of the matrix. N > 0.
 

 * @param[in] D
 
          D is REAL array, dimension (N)
          On entry, the N diagonal elements of the tridiagonal
          matrix T.
 

 * @param[in,out] E
 
          E is REAL array, dimension (N)
          On entry, the first (N-1) entries contain the subdiagonal
          elements of the tridiagonal matrix T; E(N) need not be set.
          On exit, the entries E(ISPLIT(I)), 1 <= I <= NSPLIT,
          are set to zero, the other entries of E are untouched.
 

 * @param[in,out] E2
 
          E2 is REAL array, dimension (N)
          On entry, the first (N-1) entries contain the SQUARES of the
          subdiagonal elements of the tridiagonal matrix T;
          E2(N) need not be set.
          On exit, the entries E2(ISPLIT(I)),
          1 <= I <= NSPLIT, have been set to zero
 

 * @param[in] SPLTOL
 
          SPLTOL is REAL
          The threshold for splitting. Two criteria can be used:
          SPLTOL<0 : criterion based on absolute off-diagonal value
          SPLTOL>0 : criterion that preserves relative accuracy
 

 * @param[in] TNRM
 
          TNRM is REAL
          The norm of the matrix.
 

 * @param[out] NSPLIT
 
          NSPLIT is INTEGER
          The number of blocks T splits into. 1 <= NSPLIT <= N.
 

 * @param[out] ISPLIT
 
          ISPLIT is INTEGER array, dimension (N)
          The splitting points, at which T breaks up into blocks.
          The first block consists of rows/columns 1 to ISPLIT(1),
          the second of rows/columns ISPLIT(1)+1 through ISPLIT(2),
          etc., and the NSPLIT-th consists of rows/columns
          ISPLIT(NSPLIT-1)+1 through ISPLIT(NSPLIT)=N.
 

 * @param[out] INFO
 
          INFO is INTEGER
          = 0:  successful exit
 * */         
template< typename T >
integer larra(integer *n, T *d, T *e, T *e2, T * spltol, T *tnrm, integer *nsplit, integer *isplit, integer *info)
{
  return larra(n, d, e, e2, spltol, tnrm, nsplit, isplit, info); 
}

/*! @brief LARRB provides limited bisection to locate eigenvalues for more accuracy

 * @details
 * \b Purpose:
    \verbatim 
    Given the relatively robust representation(RRR) L D L^T, SLARRB
    does "limited" bisection to refine the eigenvalues of L D L^T,
    W(IFIRST-OFFSET) through W(ILAST-OFFSET), to more accuracy. Initial
    guesses for these eigenvalues are input in W, the corresponding estimate
    of the error in these guesses and their gaps are input in WERR
    and WGAP, respectively. During bisection, intervals
    [left, right] are maintained by storing their mid-points and
    semi-widths in the arrays W and WERR respectively.
    \endverbatim 


 * @param[in] N
 
          N is INTEGER
          The order of the matrix.
 

 * @param[in] D
 
          D is REAL array, dimension (N)
          The N diagonal elements of the diagonal matrix D.
 

 * @param[in] LLD
 
          LLD is REAL array, dimension (N-1)
          The (N-1) elements L(i)*L(i)*D(i).
 

 * @param[in] IFIRST
 
          IFIRST is INTEGER
          The index of the first eigenvalue to be computed.
 

 * @param[in] ILAST
 
          ILAST is INTEGER
          The index of the last eigenvalue to be computed.
 

 * @param[in] RTOL1
 
          RTOL1 is REAL
 

 * @param[in] RTOL2
 
          RTOL2 is REAL
          Tolerance for the convergence of the bisection intervals.
          An interval [LEFT,RIGHT] has converged if
          RIGHT-LEFT < MAX(RTOL1*GAP, RTOL2*MAX(|LEFT|,|RIGHT|))
          where GAP is the (estimated) distance to the nearest
          eigenvalue.
 

 * @param[in] OFFSET
 
          OFFSET is INTEGER
          Offset for the arrays W, WGAP and WERR, i.e., the IFIRST-OFFSET
          through ILAST-OFFSET elements of these arrays are to be used.
 

 * @param[in,out] W
 
          W is REAL array, dimension (N)
          On input, W(IFIRST-OFFSET) through W(ILAST-OFFSET) are
          estimates of the eigenvalues of L D L^T indexed IFIRST through
          ILAST.
          On output, these estimates are refined.
 

 * @param[in,out] WGAP
 
          WGAP is REAL array, dimension (N-1)
          On input, the (estimated) gaps between consecutive
          eigenvalues of L D L^T, i.e., WGAP(I-OFFSET) is the gap between
          eigenvalues I and I+1. Note that if IFIRST = ILAST
          then WGAP(IFIRST-OFFSET) must be set to ZERO.
          On output, these gaps are refined.
 

 * @param[in,out] WERR
 
          WERR is REAL array, dimension (N)
          On input, WERR(IFIRST-OFFSET) through WERR(ILAST-OFFSET) are
          the errors in the estimates of the corresponding elements in W.
          On output, these errors are refined.
 

 * @param[out] WORK
 
          WORK is REAL array, dimension (2*N)
          Workspace.
 

 * @param[out] IWORK
 
          IWORK is INTEGER array, dimension (2*N)
          Workspace.
 

 * @param[in] PIVMIN
 
          PIVMIN is REAL
          The minimum pivot in the Sturm sequence.
 

 * @param[in] SPDIAM
 
          SPDIAM is REAL
          The spectral diameter of the matrix.
 

 * @param[in] TWIST
 
          TWIST is INTEGER
          The twist index for the twisted factorization that is used
          for the negcount.
          TWIST = N: Compute negcount from L D L^T - LAMBDA I = L+ D+ L+^T
          TWIST = 1: Compute negcount from L D L^T - LAMBDA I = U- D- U-^T
          TWIST = R: Compute negcount from L D L^T - LAMBDA I = N(r) D(r) N(r)
 

 * @param[out] INFO
 
          INFO is INTEGER
          Error flag. 
 * */         
template< typename T >
integer larrb(integer *n, T *d, T *lld, integer * ifirst, integer *ilast, T *rtol1, T *rtol2, integer *offset, T *w, T *wgap, T *werr, T *work, integer *iwork, T * pivmin, T *spdiam, integer *twist, integer *info)
{
  return larrb(n, d, lld,  ifirst, ilast, rtol1, rtol2, offset, w, wgap, werr, work, iwork, pivmin, spdiam, twist, info); 
}

/*! @brief LARRC computes the number of eigenvalues of the symmetric tridiagonal matrix

 * @details
 * \b Purpose:
    \verbatim 
    Find the number of eigenvalues of the symmetric tridiagonal matrix T
    that are in the interval (VL,VU] if JOBT = 'T', and of L D L^T
    if JOBT = 'L'.
    \endverbatim  

 * @param[in] JOBT
 
          JOBT is CHARACTER*1
          = 'T':  Compute Sturm count for matrix T.
          = 'L':  Compute Sturm count for matrix L D L^T.
 

 * @param[in] N
 
          N is INTEGER
          The order of the matrix. N > 0.
 

 * @param[in] VL
 
          VL is REAL
          The lower bound for the eigenvalues.
 

 * @param[in] VU
 
          VU is REAL
          The upper bound for the eigenvalues.
 

 * @param[in] D
 
          D is REAL array, dimension (N)
          JOBT = 'T': The N diagonal elements of the tridiagonal matrix T.
          JOBT = 'L': The N diagonal elements of the diagonal matrix D.
 

 * @param[in] E
 
          E is REAL array, dimension (N)
          JOBT = 'T': The N-1 offdiagonal elements of the matrix T.
          JOBT = 'L': The N-1 offdiagonal elements of the matrix L.
 

 * @param[in] PIVMIN
 
          PIVMIN is REAL
          The minimum pivot in the Sturm sequence for T.
 

 * @param[out] EIGCNT
 
          EIGCNT is INTEGER
          The number of eigenvalues of the symmetric tridiagonal matrix T
          that are in the interval (VL,VU]
 

 * @param[out] LCNT
 
          LCNT is INTEGER
 

 * @param[out] RCNT
 
          RCNT is INTEGER
          The left and right negcounts of the interval.
 

 * @param[out] INFO
 
          INFO is INTEGER 
 * */         
template< typename T >
integer larrc(char *jobt, integer *n, T *vl, T *vu, T *d, T *e, T *pivmin, integer *eigcnt, integer *lcnt, integer * rcnt, integer *info)
{
  return larrc(jobt, n, vl, vu, d, e, pivmin, eigcnt, lcnt,  rcnt, info); 
}

/*! @brief LARRD computes the eigenvalues of a symmetric tridiagonal matrix to suitable accuracy

 * @details
 * \b Purpose:
    \verbatim 
    LARRD computes the eigenvalues of a symmetric tridiagonal
    matrix T to suitable accuracy. This is an auxiliary code to be
    called from SSTEMR.
    The user may ask for all eigenvalues, all eigenvalues
    in the half-open interval (VL, VU], or the IL-th through IU-th
    eigenvalues.

    To avoid overflow, the matrix must be scaled so that its
    largest element is no greater than overflow**(1/2) * underflow**(1/4) in absolute value, and for greatest
    accuracy, it should not be much smaller than that.

    See W. Kahan "Accurate Eigenvalues of a Symmetric Tridiagonal
    Matrix", Report CS41, Computer Science Dept., Stanford
    University, July 21, 1966.
    \endverbatim 


 * @param[in] RANGE
 
          RANGE is CHARACTER*1
          = 'A': ("All")   all eigenvalues will be found.
          = 'V': ("Value") all eigenvalues in the half-open interval
                           (VL, VU] will be found.
          = 'I': ("Index") the IL-th through IU-th eigenvalues (of the
                           entire matrix) will be found.
 

 * @param[in] ORDER
 
          ORDER is CHARACTER*1
          = 'B': ("By Block") the eigenvalues will be grouped by
                              split-off block (see IBLOCK, ISPLIT) and
                              ordered from smallest to largest within
                              the block.
          = 'E': ("Entire matrix")
                              the eigenvalues for the entire matrix
                              will be ordered from smallest to
                              largest.
 

 * @param[in] N
 
          N is INTEGER
          The order of the tridiagonal matrix T.  N >= 0.
 

 * @param[in] VL
 
          VL is REAL
          If RANGE='V', the lower bound of the interval to
          be searched for eigenvalues.  Eigenvalues less than or equal
          to VL, or greater than VU, will not be   returned.  VL < VU.
          Not referenced if RANGE = 'A' or 'I'.
 

 * @param[in] VU
 
          VU is REAL
          If RANGE='V', the upper bound of the interval to
          be searched for eigenvalues.  Eigenvalues less than or equal
          to VL, or greater than VU, will not be   returned.  VL < VU.
          Not referenced if RANGE = 'A' or 'I'.
 

 * @param[in] IL
 
          IL is INTEGER
          If RANGE='I', the index of the
          smallest eigenvalue to be   returned.
          1 <= IL <= IU <= N, if N > 0; IL = 1 and IU = 0 if N = 0.
          Not referenced if RANGE = 'A' or 'V'.
 

 * @param[in] IU
 
          IU is INTEGER
          If RANGE='I', the index of the
          largest eigenvalue to be   returned.
          1 <= IL <= IU <= N, if N > 0; IL = 1 and IU = 0 if N = 0.
          Not referenced if RANGE = 'A' or 'V'.
 

 * @param[in] GERS
 
          GERS is REAL array, dimension (2*N)
          The N Gerschgorin intervals (the i-th Gerschgorin interval
          is (GERS(2*i-1), GERS(2*i)).
 

 * @param[in] RELTOL
 
          RELTOL is REAL
          The minimum relative width of an interval.  When an interval
          is narrower than RELTOL times the larger (in
          magnitude) endpoint, then it is considered to be
          sufficiently small, i.e., converged.  Note: this should
          always be at least radix*machine epsilon.
 

 * @param[in] D
 
          D is REAL array, dimension (N)
          The n diagonal elements of the tridiagonal matrix T.
 

 * @param[in] E
 
          E is REAL array, dimension (N-1)
          The (n-1) off-diagonal elements of the tridiagonal matrix T.
 

 * @param[in] E2
 
          E2 is REAL array, dimension (N-1)
          The (n-1) squared off-diagonal elements of the tridiagonal matrix T.
 

 * @param[in] PIVMIN
 
          PIVMIN is REAL
          The minimum pivot allowed in the Sturm sequence for T.
 

 * @param[in] NSPLIT
 
          NSPLIT is INTEGER
          The number of diagonal blocks in the matrix T.
          1 <= NSPLIT <= N.
 

 * @param[in] ISPLIT
 
          ISPLIT is INTEGER array, dimension (N)
          The splitting points, at which T breaks up into submatrices.
          The first submatrix consists of rows/columns 1 to ISPLIT(1),
          the second of rows/columns ISPLIT(1)+1 through ISPLIT(2),
          etc., and the NSPLIT-th consists of rows/columns
          ISPLIT(NSPLIT-1)+1 through ISPLIT(NSPLIT)=N.
          (Only the first NSPLIT elements will actually be used, but
          since the user cannot know a priori what value NSPLIT will
          have, N words must be reserved for ISPLIT.)
 

 * @param[out] M
 
          M is INTEGER
          The actual number of eigenvalues found. 0 <= M <= N.
          (See also the description of INFO=2,3.)
 

 * @param[out] W
 
          W is REAL array, dimension (N)
          On exit, the first M elements of W will contain the
          eigenvalue approximations. SLARRD computes an interval
          I_j = (a_j, b_j] that includes eigenvalue j. The eigenvalue
          approximation is given as the interval midpoint
          W(j)= (a_j + b_j)/2. The corresponding error is bounded by
          WERR(j) = abs(a_j - b_j)/2
 

 * @param[out] WERR
 
          WERR is REAL array, dimension (N)
          The error bound on the corresponding eigenvalue approximation
          in W.
 

 * @param[out] WL
 
          WL is REAL
 

 * @param[out] WU
 
          WU is REAL
          The interval (WL, WU] contains all the wanted eigenvalues.
          If RANGE='V', then WL=VL and WU=VU.
          If RANGE='A', then WL and WU are the global Gerschgorin bounds
                        on the spectrum.
          If RANGE='I', then WL and WU are computed by SLAEBZ from the
                        index range specified.
 

 * @param[out] IBLOCK
 
          IBLOCK is INTEGER array, dimension (N)
          At each row/column j where E(j) is zero or small, the
          matrix T is considered to split into a block diagonal
          matrix.  On exit, if INFO = 0, IBLOCK(i) specifies to which
          block (from 1 to the number of blocks) the eigenvalue W(i)
          belongs.  (SLARRD may use the remaining N-M elements as
          workspace.)
 

 * @param[out] INDEXW
 
          INDEXW is INTEGER array, dimension (N)
          The indices of the eigenvalues within each block (submatrix);
          for example, INDEXW(i)= j and IBLOCK(i)=k imply that the
          i-th eigenvalue W(i) is the j-th eigenvalue in block k.
 

 * @param[out] WORK
 
          WORK is REAL array, dimension (4*N)
 

 * @param[out] IWORK
 
          IWORK is INTEGER array, dimension (3*N)
 

 * @param[out] INFO
 
          INFO is INTEGER
          = 0:  successful exit
          < 0:  if INFO = -i, the i-th argument had an illegal value
          > 0:  some or all of the eigenvalues failed to converge or
                were not computed:
                =1 or 3: Bisection failed to converge for some
                        eigenvalues; these eigenvalues are flagged by a
                        negative block number.  The effect is that the
                        eigenvalues may not be as accurate as the
                        absolute and relative tolerances.  This is
                        generally caused by unexpectedly inaccurate
                        arithmetic.
                =2 or 3: RANGE='I' only: Not all of the eigenvalues
                        IL:IU were found.
                        Effect: M < IU+1-IL
                        Cause:  non-monotonic arithmetic, causing the
                                Sturm sequence to be non-monotonic.
                        Cure:   recalculate, using RANGE='A', and pick
                                out eigenvalues IL:IU.  In some cases,
                                increasing the PARAMETER "FUDGE" may
                                make things work.
                = 4:    RANGE='I', and the Gershgorin interval
                        initially used was too small.  No eigenvalues
                        were computed.
                        Probable cause: your machine has sloppy
                                        floating-point arithmetic.
                        Cure: Increase the PARAMETER "FUDGE",
                              recompile, and try again. 
 * */         
template< typename T >
integer larrd(char *range, char *order, integer *n, T *vl, T *vu, integer *il, integer *iu, T *gers, T *reltol, T * d, T *e, T *e2, T *pivmin, integer *nsplit, integer * isplit, integer *m, T *w, T *werr, T *wl, T *wu, integer * iblock, integer *indexw, T *work, integer *iwork, integer *info)
{
  return larrd(range, order, n, vl, vu, il, iu, gers, reltol, d, e, e2, pivmin, nsplit, isplit, m, w, werr, wl, wu, iblock, indexw, work, iwork, info); 
}

/*! @brief LARRE given the tridiagonal matrix T, sets small off-diagonal elements to \n
     zero and for each unreduced block Ti, finds base representations and eigenvalues
 * @details
 * \b Purpose:
    \verbatim 
    To find the desired eigenvalues of a given real symmetric
    tridiagonal matrix T, SLARRE sets any "small" off-diagonal
    elements to zero, and for each unreduced block T_i, it finds
    (a) a suitable shift at one end of the block's spectrum,
    (b) the base representation, T_i - sigma_i I = L_i D_i L_i^T, and
    (c) eigenvalues of each L_i D_i L_i^T.
    The representations and eigenvalues found are then used by
    SSTEMR to compute the eigenvectors of T.
    The accuracy varies depending on whether bisection is used to
    find a few eigenvalues or the dqds algorithm (subroutine SLASQ2) to
    conpute all and then discard any unwanted one.
    As an added benefit, SLARRE also outputs the n
    Gerschgorin intervals for the matrices L_i D_i L_i^T.
    \endverbatim 

 * @param[in] RANGE
 
          RANGE is CHARACTER*1
          = 'A': ("All")   all eigenvalues will be found.
          = 'V': ("Value") all eigenvalues in the half-open interval
                           (VL, VU] will be found.
          = 'I': ("Index") the IL-th through IU-th eigenvalues (of the
                           entire matrix) will be found.
 

 * @param[in] N
 
          N is INTEGER
          The order of the matrix. N > 0.
 

 * @param[in,out] VL
 
          VL is REAL
          If RANGE='V', the lower bound for the eigenvalues.
          Eigenvalues less than or equal to VL, or greater than VU,
          will not be   returned.  VL < VU.
          If RANGE='I' or ='A', SLARRE computes bounds on the desired
          part of the spectrum.
 

 * @param[in,out] VU
 
          VU is REAL
          If RANGE='V', the upper bound for the eigenvalues.
          Eigenvalues less than or equal to VL, or greater than VU,
          will not be   returned.  VL < VU.
          If RANGE='I' or ='A', SLARRE computes bounds on the desired
          part of the spectrum.
 

 * @param[in] IL
 
          IL is INTEGER
          If RANGE='I', the index of the
          smallest eigenvalue to be   returned.
          1 <= IL <= IU <= N.
 

 * @param[in] IU
 
          IU is INTEGER
          If RANGE='I', the index of the
          largest eigenvalue to be   returned.
          1 <= IL <= IU <= N.
 

 * @param[in,out] D
 
          D is REAL array, dimension (N)
          On entry, the N diagonal elements of the tridiagonal
          matrix T.
          On exit, the N diagonal elements of the diagonal
          matrices D_i.
 

 * @param[in,out] E
 
          E is REAL array, dimension (N)
          On entry, the first (N-1) entries contain the subdiagonal
          elements of the tridiagonal matrix T; E(N) need not be set.
          On exit, E contains the subdiagonal elements of the unit
          bidiagonal matrices L_i. The entries E(ISPLIT(I)),
          1 <= I <= NSPLIT, contain the base points sigma_i on output.
 

 * @param[in,out] E2
 
          E2 is REAL array, dimension (N)
          On entry, the first (N-1) entries contain the SQUARES of the
          subdiagonal elements of the tridiagonal matrix T;
          E2(N) need not be set.
          On exit, the entries E2(ISPLIT(I)),
          1 <= I <= NSPLIT, have been set to zero
 

 * @param[in] RTOL1
 
          RTOL1 is REAL
 

 * @param[in] RTOL2
 
          RTOL2 is REAL
           Parameters for bisection.
           An interval [LEFT,RIGHT] has converged if
           RIGHT-LEFT < MAX(RTOL1*GAP, RTOL2*MAX(|LEFT|,|RIGHT|))
 

 * @param[in] SPLTOL
 
          SPLTOL is REAL
          The threshold for splitting.
 

 * @param[out] NSPLIT
 
          NSPLIT is INTEGER
          The number of blocks T splits into. 1 <= NSPLIT <= N.
 

 * @param[out] ISPLIT
 
          ISPLIT is INTEGER array, dimension (N)
          The splitting points, at which T breaks up into blocks.
          The first block consists of rows/columns 1 to ISPLIT(1),
          the second of rows/columns ISPLIT(1)+1 through ISPLIT(2),
          etc., and the NSPLIT-th consists of rows/columns
          ISPLIT(NSPLIT-1)+1 through ISPLIT(NSPLIT)=N.
 

 * @param[out] M
 
          M is INTEGER
          The total number of eigenvalues (of all L_i D_i L_i^T)
          found.
 

 * @param[out] W
 
          W is REAL array, dimension (N)
          The first M elements contain the eigenvalues. The
          eigenvalues of each of the blocks, L_i D_i L_i^T, are
          sorted in ascending order (SLARRE may use the
          remaining N-M elements as workspace).
 

 * @param[out] WERR
 
          WERR is REAL array, dimension (N)
          The error bound on the corresponding eigenvalue in W.
 

 * @param[out] WGAP
 
          WGAP is REAL array, dimension (N)
          The separation from the right neighbor eigenvalue in W.
          The gap is only with respect to the eigenvalues of the same block
          as each block has its own representation tree.
          Exception: at the right end of a block we store the left gap
 

 * @param[out] IBLOCK
 
          IBLOCK is INTEGER array, dimension (N)
          The indices of the blocks (submatrices) associated with the
          corresponding eigenvalues in W; IBLOCK(i)=1 if eigenvalue
          W(i) belongs to the first block from the top, =2 if W(i)
          belongs to the second block, etc.
 

 * @param[out] INDEXW
 
          INDEXW is INTEGER array, dimension (N)
          The indices of the eigenvalues within each block (submatrix);
          for example, INDEXW(i)= 10 and IBLOCK(i)=2 imply that the
          i-th eigenvalue W(i) is the 10-th eigenvalue in block 2
 

 * @param[out] GERS
 
          GERS is REAL array, dimension (2*N)
          The N Gerschgorin intervals (the i-th Gerschgorin interval
          is (GERS(2*i-1), GERS(2*i)).
 

 * @param[out] PIVMIN
 
          PIVMIN is REAL
          The minimum pivot in the Sturm sequence for T.
 

 * @param[out] WORK
 
          WORK is REAL array, dimension (6*N)
          Workspace.
 

 * @param[out] IWORK
 
          IWORK is INTEGER array, dimension (5*N)
          Workspace.
 

 * @param[out] INFO
 
          INFO is INTEGER
          = 0:  successful exit
          > 0:  A problem occurred in SLARRE.
          < 0:  One of the called subroutines signaled an internal problem.
                Needs inspection of the corresponding parameter IINFO
                for further information.

          =-1:  Problem in SLARRD.
          = 2:  No base representation could be found in MAXTRY iterations.
                Increasing MAXTRY and recompilation might be a remedy.
          =-3:  Problem in SLARRB when computing the refined root
                representation for SLASQ2.
          =-4:  Problem in SLARRB when preforming bisection on the
                desired part of the spectrum.
          =-5:  Problem in SLASQ2.
          =-6:  Problem in SLASQ2. 
 * */         
template< typename T >
integer larre(char *range, integer *n, T *vl, T *vu, integer *il, integer *iu, T *d, T *e, T *e2, T *rtol1, T *rtol2, T *spltol, integer *nsplit, integer *isplit, integer * m, T *w, T *werr, T *wgap, integer *iblock, integer *indexw, T *gers, T *pivmin, T *work, integer *iwork, integer *info)
{
  return larre(range, n, vl, vu, il, iu, d, e, e2, rtol1, rtol2, spltol, nsplit, isplit, m, w, werr, wgap, iblock, indexw, gers, pivmin, work, iwork, info); 
}

/*! @brief LARRF finds a new relatively robust representation such that at least \n
     one of the eigenvalues is relatively isolated
 * @details
 * \b Purpose:
    \verbatim 
    Given the initial representation L D L^T and its cluster of close
    eigenvalues (in a relative measure), W(CLSTRT), W(CLSTRT+1), ...
    W(CLEND), SLARRF finds a new relatively robust representation
    L D L^T - SIGMA I = L(+) D(+) L(+)^T such that at least one of the
    eigenvalues of L(+) D(+) L(+)^T is relatively isolated.
    \endverbatim 

 * @param[in] N
 
          N is INTEGER
          The order of the matrix (subblock, if the matrix split).
 

 * @param[in] D
 
          D is REAL array, dimension (N)
          The N diagonal elements of the diagonal matrix D.
 

 * @param[in] L
 
          L is REAL array, dimension (N-1)
          The (N-1) subdiagonal elements of the unit bidiagonal
          matrix L.
 

 * @param[in] LD
 
          LD is REAL array, dimension (N-1)
          The (N-1) elements L(i)*D(i).
 

 * @param[in] CLSTRT
 
          CLSTRT is INTEGER
          The index of the first eigenvalue in the cluster.
 

 * @param[in] CLEND
 
          CLEND is INTEGER
          The index of the last eigenvalue in the cluster.
 

 * @param[in] W
 
          W is REAL array, dimension
          dimension is >=  (CLEND-CLSTRT+1)
          The eigenvalue APPROXIMATIONS of L D L^T in ascending order.
          W(CLSTRT) through W(CLEND) form the cluster of relatively
          close eigenalues.
 

 * @param[in,out] WGAP
 
          WGAP is REAL array, dimension
          dimension is >=  (CLEND-CLSTRT+1)
          The separation from the right neighbor eigenvalue in W.
 

 * @param[in] WERR
 
          WERR is REAL array, dimension
          dimension is >=  (CLEND-CLSTRT+1)
          WERR contain the semiwidth of the uncertainty
          interval of the corresponding eigenvalue APPROXIMATION in W
 

 * @param[in] SPDIAM
 
          SPDIAM is REAL
          estimate of the spectral diameter obtained from the
          Gerschgorin intervals
 

 * @param[in] CLGAPL
 
          CLGAPL is REAL
 

 * @param[in] CLGAPR
 
          CLGAPR is REAL
          absolute gap on each end of the cluster.
          Set by the calling routine to protect against shifts too close
          to eigenvalues outside the cluster.
 

 * @param[in] PIVMIN
 
          PIVMIN is REAL
          The minimum pivot allowed in the Sturm sequence.
 

 * @param[out] SIGMA
 
          SIGMA is REAL
          The shift used to form L(+) D(+) L(+)^T.
 

 * @param[out] DPLUS
 
          DPLUS is REAL array, dimension (N)
          The N diagonal elements of the diagonal matrix D(+).
 

 * @param[out] LPLUS
 
          LPLUS is REAL array, dimension (N-1)
          The first (N-1) elements of LPLUS contain the subdiagonal
          elements of the unit bidiagonal matrix L(+).
 

 * @param[out] WORK
 
          WORK is REAL array, dimension (2*N)
          Workspace.
 

 * @param[out] INFO
 
          INFO is INTEGER
          Signals processing OK (=0) or failure (=1)  
 * */         
template< typename T >
integer larrf(integer *n, T *d, T *l, T *ld, integer *clstrt, integer *clend, T *w, T *wgap, T *werr, T *spdiam, T *clgapl, T *clgapr, T *pivmin, T *sigma, T *dplus, T *lplus, T *work, integer *info)
{
  return larrf(n, d, l, ld, clstrt, clend, w, wgap, werr, spdiam, clgapl, clgapr, pivmin, sigma, dplus, lplus, work, info); 
}

/*! @brief LARRJ performs refinement of the initial estimates of the eigenvalues of the matrix T

 * @details
 * \b Purpose:
    \verbatim 
    Given the initial eigenvalue approximations of T, SLARRJ
    does  bisection to refine the eigenvalues of T,
    W(IFIRST-OFFSET) through W(ILAST-OFFSET), to more accuracy. Initial
    guesses for these eigenvalues are input in W, the corresponding estimate
    of the error in these guesses in WERR. During bisection, intervals
    [left, right] are maintained by storing their mid-points and
    semi-widths in the arrays W and WERR respectively.
    \endverbatim 

 * @param[in] N
 
          N is INTEGER
          The order of the matrix.
 

 * @param[in] D
 
          D is REAL array, dimension (N)
          The N diagonal elements of T.
 

 * @param[in] E2
 
          E2 is REAL array, dimension (N-1)
          The Squares of the (N-1) subdiagonal elements of T.
 

 * @param[in] IFIRST
 
          IFIRST is INTEGER
          The index of the first eigenvalue to be computed.
 

 * @param[in] ILAST
 
          ILAST is INTEGER
          The index of the last eigenvalue to be computed.
 

 * @param[in] RTOL
 
          RTOL is REAL
          Tolerance for the convergence of the bisection intervals.
          An interval [LEFT,RIGHT] has converged if
          RIGHT-LEFT < RTOL*MAX(|LEFT|,|RIGHT|).
 

 * @param[in] OFFSET
 
          OFFSET is INTEGER
          Offset for the arrays W and WERR, i.e., the IFIRST-OFFSET
          through ILAST-OFFSET elements of these arrays are to be used.
 

 * @param[in,out] W
 
          W is REAL array, dimension (N)
          On input, W(IFIRST-OFFSET) through W(ILAST-OFFSET) are
          estimates of the eigenvalues of L D L^T indexed IFIRST through
          ILAST.
          On output, these estimates are refined.
 

 * @param[in,out] WERR
 
          WERR is REAL array, dimension (N)
          On input, WERR(IFIRST-OFFSET) through WERR(ILAST-OFFSET) are
          the errors in the estimates of the corresponding elements in W.
          On output, these errors are refined.
 

 * @param[out] WORK
 
          WORK is REAL array, dimension (2*N)
          Workspace.
 

 * @param[out] IWORK
 
          IWORK is INTEGER array, dimension (2*N)
          Workspace.
 

 * @param[in] PIVMIN
 
          PIVMIN is REAL
          The minimum pivot in the Sturm sequence for T.
 

 * @param[in] SPDIAM
 
          SPDIAM is REAL
          The spectral diameter of T.
 

 * @param[out] INFO
 
          INFO is INTEGER
          Error flag.
 * */         
template< typename T >
integer larrj(integer *n, T *d, T *e2, integer *ifirst, integer *ilast, T *rtol, integer *offset, T *w, T *werr, T *work, integer *iwork, T *pivmin, T *spdiam, integer *info)
{
  return larrj(n, d, e2, ifirst, ilast, rtol, offset, w, werr, work, iwork, pivmin, spdiam, info); 
}

/*! @brief LARRK computes one eigenvalue of a symmetric tridiagonal matrix T to suitable accuracy

 * @details
 * \b Purpose:
    \verbatim 
    LARRK computes one eigenvalue of a symmetric tridiagonal
    matrix T to suitable accuracy. This is an auxiliary code to be
    called from SSTEMR.

    To avoid overflow, the matrix must be scaled so that its
    largest element is no greater than overflow**(1/2) * underflow**(1/4) in absolute value, and for greatest
    accuracy, it should not be much smaller than that.

    See W. Kahan "Accurate Eigenvalues of a Symmetric Tridiagonal
    Matrix", Report CS41, Computer Science Dept., Stanford
    University, July 21, 1966.
    \endverbatim 

 * @param[in] N
 
          N is INTEGER
          The order of the tridiagonal matrix T.  N >= 0.
 

 * @param[in] IW
 
          IW is INTEGER
          The index of the eigenvalues to be   returned.
 

 * @param[in] GL
 
          GL is REAL
 

 * @param[in] GU
 
          GU is REAL
          An upper and a lower bound on the eigenvalue.
 

 * @param[in] D
 
          D is REAL array, dimension (N)
          The n diagonal elements of the tridiagonal matrix T.
 

 * @param[in] E2
 
          E2 is REAL array, dimension (N-1)
          The (n-1) squared off-diagonal elements of the tridiagonal matrix T.
 

 * @param[in] PIVMIN
 
          PIVMIN is REAL
          The minimum pivot allowed in the Sturm sequence for T.
 

 * @param[in] RELTOL
 
          RELTOL is REAL
          The minimum relative width of an interval.  When an interval
          is narrower than RELTOL times the larger (in
          magnitude) endpoint, then it is considered to be
          sufficiently small, i.e., converged.  Note: this should
          always be at least radix*machine epsilon.
 

 * @param[out] W
 
          W is REAL
 

 * @param[out] WERR
 
          WERR is REAL
          The error bound on the corresponding eigenvalue approximation
          in W.
 

 * @param[out] INFO
 
          INFO is INTEGER
          = 0:       Eigenvalue converged
          = -1:      Eigenvalue did NOT converge 
 * */         
template< typename T >
integer larrk(integer *n, integer *iw, T *gl, T *gu, T *d, T *e2, T *pivmin, T *reltol, T *w, T *werr, integer *info)
{
  return larrk(n, iw, gl, gu, d, e2, pivmin, reltol, w, werr, info); 
}

/*! @brief LARRR performs tests to decide whether the symmetric tridiagonal matrix T \n
     warrants expensive computations which guarantee high relative accuracy in the   \n
     eigenvalues
 * @details
 * \b Purpose:
    \verbatim 
    Perform tests to decide whether the symmetric tridiagonal matrix T
    warrants expensive computations which guarantee high relative accuracy
    in the eigenvalues.
    \endverbatim 

 * @param[in] N
 
          N is INTEGER
          The order of the matrix. N > 0.
 

 * @param[in] D
 
          D is REAL array, dimension (N)
          The N diagonal elements of the tridiagonal matrix T.
 

 * @param[in,out] E
 
          E is REAL array, dimension (N)
          On entry, the first (N-1) entries contain the subdiagonal
          elements of the tridiagonal matrix T; E(N) is set to ZERO.
 

 * @param[out] INFO
 
          INFO is INTEGER
          INFO = 0(default) : the matrix warrants computations preserving
                              relative accuracy.
          INFO = 1          : the matrix warrants computations guaranteeing
                              only absolute accuracy. 
 * */         
template< typename T >
integer larrr(integer *n, T *d, T *e, integer *info)
{
  return larrr(n, d, e, info);   
}

/*! @brief LARRV computes the eigenvectors of the tridiagonal matrix T = L D LT    \n
     given L, D and the eigenvalues of L D LT
 * @details
 * \b Purpose:
    \verbatim 
    LARRV computes the eigenvectors of the tridiagonal matrix
    T = L D L**T given L, D and APPROXIMATIONS to the eigenvalues of L D L**T.
    The input eigenvalues should have been computed by LARRE.
    \endverbatim 

 * @param[in] N
 
          N is INTEGER
          The order of the matrix.  N >= 0.
 

 * @param[in] VL
 
          VL is REAL
          Lower bound of the interval that contains the desired
          eigenvalues. VL < VU. Needed to compute gaps on the left or right
          end of the extremal eigenvalues in the desired RANGE.
 

 * @param[in] VU
 
          VU is REAL
          Upper bound of the interval that contains the desired
          eigenvalues. VL < VU. 
          Note: VU is currently not used by this implementation of SLARRV, VU is
          passed to SLARRV because it could be used compute gaps on the right end
          of the extremal eigenvalues. However, with not much initial accuracy in
          LAMBDA and VU, the formula can lead to an overestimation of the right gap
          and thus to inadequately early RQI 'convergence'. This is currently
          prevented this by forcing a small right gap. And so it turns out that VU
          is currently not used by this implementation of SLARRV.
 

 * @param[in,out] D
 
          D is REAL array, dimension (N)
          On entry, the N diagonal elements of the diagonal matrix D.
          On exit, D may be overwritten.
 

 * @param[in,out] L
 
          L is REAL array, dimension (N)
          On entry, the (N-1) subdiagonal elements of the unit
          bidiagonal matrix L are in elements 1 to N-1 of L
          (if the matrix is not split.) At the end of each block
          is stored the corresponding shift as given by SLARRE.
          On exit, L is overwritten.
 

 * @param[in] PIVMIN
 
          PIVMIN is REAL
          The minimum pivot allowed in the Sturm sequence.
 

 * @param[in] ISPLIT
 
          ISPLIT is INTEGER array, dimension (N)
          The splitting points, at which T breaks up into blocks.
          The first block consists of rows/columns 1 to
          ISPLIT(1), the second of rows/columns ISPLIT(1)+1
          through ISPLIT(2), etc.
 

 * @param[in] M
 
          M is INTEGER
          The total number of input eigenvalues.  0 <= M <= N.
 

 * @param[in] DOL
 
          DOL is INTEGER
 

 * @param[in] DOU
 
          DOU is INTEGER
          If the user wants to compute only selected eigenvectors from all
          the eigenvalues supplied, he can specify an index range DOL:DOU.
          Or else the setting DOL=1, DOU=M should be applied.
          Note that DOL and DOU refer to the order in which the eigenvalues
          are stored in W.
          If the user wants to compute only selected eigenpairs, then
          the columns DOL-1 to DOU+1 of the eigenvector space Z contain the
          computed eigenvectors. All other columns of Z are set to zero.
 

 * @param[in] MINRGP
 
          MINRGP is REAL
 

 * @param[in] RTOL1
 
          RTOL1 is REAL
 

 * @param[in] RTOL2
 
          RTOL2 is REAL
           Parameters for bisection.
           An interval [LEFT,RIGHT] has converged if
           RIGHT-LEFT < MAX(RTOL1*GAP, RTOL2*MAX(|LEFT|,|RIGHT|))
 

 * @param[in,out] W
 
          W is REAL array, dimension (N)
          The first M elements of W contain the APPROXIMATE eigenvalues for
          which eigenvectors are to be computed.  The eigenvalues
          should be grouped by split-off block and ordered from
          smallest to largest within the block (The output array
          W from SLARRE is expected here). Furthermore, they are with
          respect to the shift of the corresponding root representation
          for their block. On exit, W holds the eigenvalues of the
          UNshifted matrix.
 

 * @param[in,out] WERR
 
          WERR is REAL array, dimension (N)
          The first M elements contain the semiwidth of the uncertainty
          interval of the corresponding eigenvalue in W
 

 * @param[in,out] WGAP
 
          WGAP is REAL array, dimension (N)
          The separation from the right neighbor eigenvalue in W.
 

 * @param[in] IBLOCK
 
          IBLOCK is INTEGER array, dimension (N)
          The indices of the blocks (submatrices) associated with the
          corresponding eigenvalues in W; IBLOCK(i)=1 if eigenvalue
          W(i) belongs to the first block from the top, =2 if W(i)
          belongs to the second block, etc.
 

 * @param[in] INDEXW
 
          INDEXW is INTEGER array, dimension (N)
          The indices of the eigenvalues within each block (submatrix);
          for example, INDEXW(i)= 10 and IBLOCK(i)=2 imply that the
          i-th eigenvalue W(i) is the 10-th eigenvalue in the second block.
 

 * @param[in] GERS
 
          GERS is REAL array, dimension (2*N)
          The N Gerschgorin intervals (the i-th Gerschgorin interval
          is (GERS(2*i-1), GERS(2*i)). The Gerschgorin intervals should
          be computed from the original UNshifted matrix.
 

 * @param[out] Z
 
          Z is REAL array, dimension (LDZ, max(1,M))
          If INFO = 0, the first M columns of Z contain the
          orthonormal eigenvectors of the matrix T
          corresponding to the input eigenvalues, with the i-th
          column of Z holding the eigenvector associated with W(i).
          Note: the user must ensure that at least max(1,M) columns are
          supplied in the array Z.
 

 * @param[in] LDZ
 
          LDZ is INTEGER
          The leading dimension of the array Z.  LDZ >= 1, and if
          JOBZ = 'V', LDZ >= max(1,N).
 

 * @param[out] ISUPPZ
 
          ISUPPZ is INTEGER array, dimension (2*max(1,M))
          The support of the eigenvectors in Z, i.e., the indices
          indicating the nonzero elements in Z. The I-th eigenvector
          is nonzero only in elements ISUPPZ(2*I-1) through
          ISUPPZ(2*I).
 

 * @param[out] WORK
 
          WORK is REAL array, dimension (12*N)
 

 * @param[out] IWORK
 
          IWORK is INTEGER array, dimension (7*N)
 

 * @param[out] INFO
 
          INFO is INTEGER
          = 0:  successful exit

          > 0:  A problem occurred in SLARRV.
          < 0:  One of the called subroutines signaled an internal problem.
                Needs inspection of the corresponding parameter IINFO
                for further information.

          =-1:  Problem in SLARRB when refining a child's eigenvalues.
          =-2:  Problem in SLARRF when computing the RRR of a child.
                When a child is inside a tight cluster, it can be difficult
                to find an RRR. A partial remedy from the user's point of
                view is to make the parameter MINRGP smaller and recompile.
                However, as the orthogonality of the computed vectors is
                proportional to 1/MINRGP, the user should be aware that
                he might be trading in precision when he decreases MINRGP.
          =-3:  Problem in SLARRB when refining a single eigenvalue
                after the Rayleigh correction was rejected.
          = 5:  The Rayleigh Quotient Iteration failed to converge to
                full accuracy in MAXITR steps. 
 * */         
template< typename T >
integer larrv(integer *n, T *vl, T *vu, T *d, T *l, T *pivmin, integer *isplit, integer *m, integer *dol, integer *dou, T *minrgp, T *rtol1, T *rtol2, T *w, T *werr, T *wgap, integer *iblock, integer *indexw, T *gers, T *z, integer *ldz, integer *isuppz, T *work, integer *iwork, integer * info)
{
  return larrv(n, vl, vu, d, l, pivmin, isplit, m, dol, dou, minrgp, rtol1, rtol2, w, werr, wgap, iblock, indexw, gers, z, ldz, isuppz, work, iwork, info);
}
template< typename T, typename Ta >
integer larrv(integer *n, Ta *vl, Ta *vu, Ta *d, Ta *l, Ta *pivmin, integer *isplit, integer *m, integer *dol, integer *dou, Ta *minrgp, Ta *rtol1, Ta *rtol2, Ta *w, Ta *werr, Ta *wgap, integer *iblock, integer *indexw, Ta *gers, T *z, integer *ldz, integer *isuppz, Ta *work, integer *iwork, integer * info)
{
  return larrv(n, vl, vu, d, l, pivmin, isplit, m, dol, dou, minrgp, rtol1, rtol2, w, werr, wgap, iblock, indexw, gers, z, ldz, isuppz, work, iwork, info);
}

/*! @brief LARSCL2 performs reciprocal diagonal scaling on a vector

 * @details
 * \b Purpose:
    \verbatim 
    LARSCL2 performs a reciprocal diagonal scaling on an vector:
      x <-- inv(D) * x
    where the diagonal matrix D is stored as a vector.

    Eventually to be replaced by BLAS_sge_diag_scale in the new BLAS
    standard.
    \endverbatim 

 * @param[in] M
 
          M is INTEGER
     The number of rows of D and X. M >= 0.
 

 * @param[in] N
 
          N is INTEGER
     The number of columns of X. N >= 0.
 

 * @param[in] D
 
          D is REAL array, length M
     Diagonal matrix D, stored as a vector of length M.
 

 * @param[in,out] X
 
          X is REAL array, dimension (LDX,N)
     On entry, the vector X to be scaled by D.
     On exit, the scaled vector.
 

 * @param[in] LDX
 
          LDX is INTEGER
     The leading dimension of the vector X. LDX >= M. 
 * */         
template< typename T >
integer larscl2(integer *m, integer *n, T *d, T *x, integer *ldx)
{
  return larscl2(m, n, d, x, ldx);
}
template< typename T, typename Ta >
integer larscl2(integer *m, integer *n, Ta *d, T *x, integer *ldx)
{
  return larscl2(m, n, d, x, ldx);
}

/*! @brief LARTG generates a plane rotation with real cosine and real sine

 * @details
 * \b Purpose:
    \verbatim 
    LARTG generate a plane rotation so that

       [  CS  SN  ]  .  [ F ]  =  [ R ]   where CS**2 + SN**2 = 1.
       [ -SN  CS  ]     [ G ]     [ 0 ]

    This is a slower, more accurate version of the BLAS1 routine SROTG,
    with the following other differences:
       F and G are unchanged on   return.
       If G=0, then CS=1 and SN=0.
       If F=0 and (G .ne. 0), then CS=0 and SN=1 without doing any
          floating point operations (saves work in SBDSQR when
          there are zeros on the diagonal).

    If F exceeds G in magnitude, CS will be positive.
    \endverbatim  

 * @param[in] F
 
          F is REAL
          The first component of vector to be rotated.
 

 * @param[in] G
 
          G is REAL
          The second component of vector to be rotated.
 

 * @param[out] CS
 
          CS is REAL
          The cosine of the rotation.
 

 * @param[out] SN
 
          SN is REAL
          The sine of the rotation.
 

 * @param[out] R
 
          R is REAL
          The nonzero component of the rotated vector.

  This version has a few statements commented out for thread safety
  (machine parameters are computed on each entry). 10 feb 03, SJH.
  
 * */         
template< typename T >
integer lartg(T f, T g, T *cs, T *sn, T *r__)
{
  return lartg(f, g, cs, sn, r__);
}
template< typename T, typename Ta >
integer lartg(T f, T g, Ta *cs, T *sn, T *r__)
{
  return lartg(f, g, cs, sn, r__);
}

/*! @brief LARTV applies a vector of plane rotations with real cosines and \n
     real sines to the elements of a pair of vectors
 * @details
 * \b Purpose:
    \verbatim 
    LARTV applies a vector of real plane rotations to elements of the
    real vectors x and y. For i = 1,2,...,n

       (x(i)) := ( c(i)  s(i)) (x(i))
       (y(i))    (-s(i)  c(i)) (y(i))
    \endverbatim 

 * @param[in] N
 
          N is INTEGER
          The number of plane rotations to be applied.
 

 * @param[in,out] X
 
          X is REAL array,
                         dimension (1+(N-1)*INCX)
          The vector x.
 

 * @param[in] INCX
 
          INCX is INTEGER
          The increment between elements of X. INCX > 0.
 

 * @param[in,out] Y
 
          Y is REAL array,
                         dimension (1+(N-1)*INCY)
          The vector y.
 

 * @param[in] INCY
 
          INCY is INTEGER
          The increment between elements of Y. INCY > 0.
 

 * @param[in] C
 
          C is REAL array, dimension (1+(N-1)*INCC)
          The cosines of the plane rotations.
 

 * @param[in] S
 
          S is REAL array, dimension (1+(N-1)*INCC)
          The sines of the plane rotations.
 

 * @param[in] INCC
 
          INCC is INTEGER
          The increment between elements of C and S. INCC > 0.   
 * */         
template< typename T >
integer lartv(integer *n, T *x, integer *incx, T *y, integer *incy, T *c, T *s, integer *incc)
{
  return lartv(n, x, incx, y, incy, c, s, incc);
}
template< typename T, typename Ta >
integer lartv(integer *n, T *x, integer *incx, T *y, integer *incy, Ta *c, T *s, integer *incc)
{
  return lartv(n, x, incx, y, incy, c, s, incc);
}

/*! @brief LARUV   returns a vector of n random real numbers from a uniform distribution

 * @details
 * \b Purpose:
    \verbatim 
    LARUV   returns a vector of n random real numbers from a uniform (0,1)
    distribution (n <= 128).

    This is an auxiliary routine called by SLARNV and CLARNV.
    \endverbatim 

 * @param[in,out] ISEED
 
          ISEED is INTEGER array, dimension (4)
          On entry, the seed of the random number generator; the array
          elements must be between 0 and 4095, and ISEED(4) must be
          odd.
          On exit, the seed is updated.
 

 * @param[in] N
 
          N is INTEGER
          The number of random numbers to be generated. N <= 128.
 

 * @param[out] X
 
          X is REAL array, dimension (N)
          The generated random numbers.   
 * */         
template< typename T >
integer laruv(integer *iseed, integer *n, T *x)
{
    return laruv(iseed, n, x);  
}

/*! @brief LARZ applies an elementary reflector (as   returned by stzrzf) to a general matrix

 * @details
 * \b Purpose:
    \verbatim 
    LARZ applies a real elementary reflector H to a real M-by-N
    matrix C, from either the left or the right. H is represented in the
    form

          H = I - tau * v * v**T

    where tau is a real scalar and v is a real vector.

    If tau = 0, then H is taken to be the unit matrix.


    H is a product of k elementary reflectors as   returned by STZRZF.
    \endverbatim 

 * @param[in] SIDE
 
          SIDE is CHARACTER*1
          = 'L': form  H * C
          = 'R': form  C * H
 

 * @param[in] M
 
          M is INTEGER
          The number of rows of the matrix C.
 

 * @param[in] N
 
          N is INTEGER
          The number of columns of the matrix C.
 

 * @param[in] L
 
          L is INTEGER
          The number of entries of the vector V containing
          the meaningful part of the Householder vectors.
          If SIDE = 'L', M >= L >= 0, if SIDE = 'R', N >= L >= 0.
 

 * @param[in] V
 
          V is REAL array, dimension (1+(L-1)*abs(INCV))
          The vector v in the representation of H as   returned by
          STZRZF. V is not used if TAU = 0.
 

 * @param[in] INCV
 
          INCV is INTEGER
          The increment between elements of v. INCV <> 0.
 

 * @param[in] TAU
 
          TAU is REAL
          The value tau in the representation of H.
 

 * @param[in,out] C
 
          C is REAL array, dimension (LDC,N)
          On entry, the M-by-N matrix C.
          On exit, C is overwritten by the matrix H * C if SIDE = 'L',
          or C * H if SIDE = 'R'.
 

 * @param[in] LDC
 
          LDC is INTEGER
          The leading dimension of the array C. LDC >= max(1,M).
 

 * @param[out] WORK
 
          WORK is REAL array, dimension
                         (N) if SIDE = 'L'
                      or (M) if SIDE = 'R'  
 * */         
template< typename T >
integer larz(char *side, integer *m, integer *n, integer *l, T *v, integer *incv, T *tau, T *c, integer *ldc, T * work)
{
  return larz(side, m, n, l, v, incv, tau, c, ldc, work);
}

/*! @brief LARZB applies a block reflector or its transpose to a general matrix

 * @details
 * \b Purpose:
    \verbatim 
    LARZB applies a real block reflector H or its transpose H**T to
    a real distributed M-by-N  C from the left or the right.

    Currently, only STOREV = 'R' and DIRECT = 'B' are supported.
    \endverbatim 

 * @param[in] SIDE
 
          SIDE is CHARACTER*1
          = 'L': apply H or H**T from the Left
          = 'R': apply H or H**T from the Right
 

 * @param[in] TRANS
 
          TRANS is CHARACTER*1
          = 'N': apply H (No transpose)
          = 'C': apply H**T (Transpose)
 

 * @param[in] DIRECT
 
          DIRECT is CHARACTER*1
          Indicates how H is formed from a product of elementary
          reflectors
          = 'F': H = H(1) H(2) . . . H(k) (Forward, not supported yet)
          = 'B': H = H(k) . . . H(2) H(1) (Backward)
 

 * @param[in] STOREV
 
          STOREV is CHARACTER*1
          Indicates how the vectors which define the elementary
          reflectors are stored:
          = 'C': Columnwise                        (not supported yet)
          = 'R': Rowwise
 

 * @param[in] M
 
          M is INTEGER
          The number of rows of the matrix C.
 

 * @param[in] N
 
          N is INTEGER
          The number of columns of the matrix C.
 

 * @param[in] K
 
          K is INTEGER
          The order of the matrix T (= the number of elementary
          reflectors whose product defines the block reflector).
 

 * @param[in] L
 
          L is INTEGER
          The number of columns of the matrix V containing the
          meaningful part of the Householder reflectors.
          If SIDE = 'L', M >= L >= 0, if SIDE = 'R', N >= L >= 0.
 

 * @param[in] V
 
          V is REAL array, dimension (LDV,NV).
          If STOREV = 'C', NV = K; if STOREV = 'R', NV = L.
 

 * @param[in] LDV
 
          LDV is INTEGER
          The leading dimension of the array V.
          If STOREV = 'C', LDV >= L; if STOREV = 'R', LDV >= K.
 

 * @param[in] T
 
          T is REAL array, dimension (LDT,K)
          The triangular K-by-K matrix T in the representation of the
          block reflector.
 

 * @param[in] LDT
 
          LDT is INTEGER
          The leading dimension of the array T. LDT >= K.
 

 * @param[in,out] C
 
          C is REAL array, dimension (LDC,N)
          On entry, the M-by-N matrix C.
          On exit, C is overwritten by H*C or H**T*C or C*H or C*H**T.
 

 * @param[in] LDC
 
          LDC is INTEGER
          The leading dimension of the array C. LDC >= max(1,M).
 

 * @param[out] WORK
 
          WORK is REAL array, dimension (LDWORK,K)
 

 * @param[in] LDWORK
 
          LDWORK is INTEGER
          The leading dimension of the array WORK.
          If SIDE = 'L', LDWORK >= max(1,N);
          if SIDE = 'R', LDWORK >= max(1,M).
 * */         
template< typename T >
integer larzb(char *side, char *trans, char *direct, char * storev, integer *m, integer *n, integer *k, integer *l, T *v, integer *ldv, T *t, integer *ldt, T *c, integer *ldc, T * work, integer *ldwork)
{
  return larzb(side, trans, direct, storev, m, n, k, l, v, ldv, t, ldt, c, ldc, work, ldwork);
}

/*! @brief LARZT forms the triangular factor T of a block reflector H = I - vtvH.

 * @details
 * \b Purpose:
    \verbatim 
    LARZT forms the triangular factor T of a real block reflector
    H of order > n, which is defined as a product of k elementary
    reflectors.

    If DIRECT = 'F', H = H(1) H(2) . . . H(k) and T is upper triangular;

    If DIRECT = 'B', H = H(k) . . . H(2) H(1) and T is lower triangular.

    If STOREV = 'C', the vector which defines the elementary reflector
    H(i) is stored in the i-th column of the array V, and

       H  =  I - V * T * V**T

    If STOREV = 'R', the vector which defines the elementary reflector
    H(i) is stored in the i-th row of the array V, and

       H  =  I - V**T * T * V

    Currently, only STOREV = 'R' and DIRECT = 'B' are supported.
    \endverbatim 

 * @param[in] DIRECT
 
          DIRECT is CHARACTER*1
          Specifies the order in which the elementary reflectors are
          multiplied to form the block reflector:
          = 'F': H = H(1) H(2) . . . H(k) (Forward, not supported yet)
          = 'B': H = H(k) . . . H(2) H(1) (Backward)
 

 * @param[in] STOREV
 
          STOREV is CHARACTER*1
          Specifies how the vectors which define the elementary
          reflectors are stored (see also Further Details):
          = 'C': columnwise                        (not supported yet)
          = 'R': rowwise
 

 * @param[in] N
 
          N is INTEGER
          The order of the block reflector H. N >= 0.
 

 * @param[in] K
 
          K is INTEGER
          The order of the triangular factor T (= the number of
          elementary reflectors). K >= 1.
 

 * @param[in,out] V
 
          V is REAL array, dimension
                               (LDV,K) if STOREV = 'C'
                               (LDV,N) if STOREV = 'R'
          The matrix V. See further details.
 

 * @param[in] LDV
 
          LDV is INTEGER
          The leading dimension of the array V.
          If STOREV = 'C', LDV >= max(1,N); if STOREV = 'R', LDV >= K.
 

 * @param[in] TAU
 
          TAU is REAL array, dimension (K)
          TAU(i) must contain the scalar factor of the elementary
          reflector H(i).
 

 * @param[out] T
 
          T is REAL array, dimension (LDT,K)
          The k by k triangular factor T of the block reflector.
          If DIRECT = 'F', T is upper triangular; if DIRECT = 'B', T is
          lower triangular. The rest of the array is not used.
 

 * @param[in] LDT
 
          LDT is INTEGER
          The leading dimension of the array T. LDT >= K.
 * */         
template< typename T >
integer larzt(char *direct, char *storev, integer *n, integer * k, T *v, integer *ldv, T *tau, T *t, integer *ldt)
{
  return larzt(direct, storev, n, k, v, ldv, tau, t, ldt);
}

/*! @brief LAS2 computes singular values of a 2-by-2 triangular matrix

 * @details
 * \b Purpose:
    \verbatim 
    LAS2  computes the singular values of the 2-by-2 matrix
       [  F   G  ]
       [  0   H  ].
    On   return, SSMIN is the smaller singular value and SSMAX is the
    larger singular value.
    \endverbatim 

 * @param[in] F
 
          F is REAL
          The (1,1) element of the 2-by-2 matrix.
 

 * @param[in] G
 
          G is REAL
          The (1,2) element of the 2-by-2 matrix.
 

 * @param[in] H
 
          H is REAL
          The (2,2) element of the 2-by-2 matrix.
 

 * @param[out] SSMIN
 
          SSMIN is REAL
          The smaller singular value.
 

 * @param[out] SSMAX
 
          SSMAX is REAL
          The larger singular value. 
 * */         
template< typename T >
integer las2(T *f, T *g, T *h, T *ssmin, T * ssmax)
{
  return las2(f, g, h, ssmin, ssmax); 
}

/*! @brief LASCL2 performs diagonal scaling on a vector

 * @details
 * \b Purpose:
    \verbatim 
    LASCL2 performs a diagonal scaling on a vector:
      x <-- D * x
    where the diagonal matrix D is stored as a vector.

    Eventually to be replaced by BLAS_sge_diag_scale in the new BLAS
    standard.
    \endverbatim 

 * @param[in] M
 
          M is INTEGER
     The number of rows of D and X. M >= 0.
 

 * @param[in] N
 
          N is INTEGER
     The number of columns of X. N >= 0.
 

 * @param[in] D
 
          D is REAL array, length M
     Diagonal matrix D, stored as a vector of length M.
 

 * @param[in,out] X
 
          X is REAL array, dimension (LDX,N)
     On entry, the vector X to be scaled by D.
     On exit, the scaled vector.
 

 * @param[in] LDX
 
          LDX is INTEGER
     The leading dimension of the vector X. LDX >= M. 
 * */         
template< typename T >
integer lascl2(integer *m, integer *n, T *d, T *x, integer *ldx)
{
  return lascl2(m, n, d, x, ldx);
}
template< typename T, typename Ta >
integer lascl2(integer *m, integer *n, Ta *d, T *x, integer *ldx)
{
  return lascl2(m, n, d, x, ldx);
}

/*! @brief LASD0 computes the singular values of a real upper bidiagonal n-by-m  \n
     matrix B with diagonal d and off-diagonal e. Used by sbdsdc
 * @details
 * \b Purpose:
    \verbatim 
    Using a divide and conquer approach, SLASD0 computes the singular
    value decomposition (SVD) of a real upper bidiagonal N-by-M
    matrix B with diagonal D and offdiagonal E, where M = N + SQRE.
    The algorithm computes orthogonal matrices U and VT such that
    B = U * S * VT. The singular values S are overwritten on D.

    A related subroutine, SLASDA, computes only the singular values,
    and optionally, the singular vectors in compact form.
    \endverbatim 

 * @param[in] N
 
          N is INTEGER
         On entry, the row dimension of the upper bidiagonal matrix.
         This is also the dimension of the main diagonal array D.
 

 * @param[in] SQRE
 
          SQRE is INTEGER
         Specifies the column dimension of the bidiagonal matrix.
         = 0: The bidiagonal matrix has column dimension M = N;
         = 1: The bidiagonal matrix has column dimension M = N+1;
 

 * @param[in,out] D
 
          D is REAL array, dimension (N)
         On entry D contains the main diagonal of the bidiagonal
         matrix.
         On exit D, if INFO = 0, contains its singular values.
 

 * @param[in,out] E
 
          E is REAL array, dimension (M-1)
         Contains the subdiagonal entries of the bidiagonal matrix.
         On exit, E has been destroyed.
 

 * @param[out] U
 
          U is REAL array, dimension (LDU, N)
         On exit, U contains the left singular vectors.
 

 * @param[in] LDU
 
          LDU is INTEGER
         On entry, leading dimension of U.
 

 * @param[out] VT
 
          VT is REAL array, dimension (LDVT, M)
         On exit, VT**T contains the right singular vectors.
 

 * @param[in] LDVT
 
          LDVT is INTEGER
         On entry, leading dimension of VT.
 

 * @param[in] SMLSIZ
 
          SMLSIZ is INTEGER
         On entry, maximum size of the subproblems at the
         bottom of the computation tree.
 

 * @param[out] IWORK
 
          IWORK is INTEGER array, dimension (8*N)
 

 * @param[out] WORK
 
          WORK is REAL array, dimension (3*M**2+2*M)
 

 * @param[out] INFO
 
          INFO is INTEGER
          = 0:  successful exit.
          < 0:  if INFO = -i, the i-th argument had an illegal value.
          > 0:  if INFO = 1, a singular value did not converge 
 * */         
template< typename T >
integer lasd0(integer *n, integer *sqre, T *d, T *e, T *u, integer *ldu, T *vt, integer *ldvt, integer *smlsiz, integer *iwork, T *work, integer *info)
{
  return lasd0(n, sqre, d, e, u, ldu, vt, ldvt, smlsiz, iwork, work, info); 
}

/*! @brief LASD1 computes the SVD of an upper bidiagonal matrix B of the specified size. Used by sbdsdc

 * @details
 * \b Purpose:
    \verbatim 
    LASD1 computes the SVD of an upper bidiagonal N-by-M matrix B,
    where N = NL + NR + 1 and M = N + SQRE. SLASD1 is called from SLASD0.

    A related subroutine SLASD7 handles the case in which the singular
    values (and the singular vectors in factored form) are desired.

    SLASD1 computes the SVD as follows:

                  (D1(in)    0    0       0)
      B = U(in) * (  Z1**T   a   Z2**T    b) * VT(in)
                  (  0       0   D2(in)   0)

        = U(out) * (D(out) 0) * VT(out)

    where Z**T = (Z1**T a Z2**T b) = u**T VT**T, and u is a vector of dimension M
    with ALPHA and BETA in the NL+1 and NL+2 th entries and zeros
    elsewhere; and the entry b is empty if SQRE = 0.

    The left singular vectors of the original matrix are stored in U, and
    the transpose of the right singular vectors are stored in VT, and the
    singular values are in D.  The algorithm consists of three stages:

       The first stage consists of deflating the size of the problem
       when there are multiple singular values or when there are zeros in
       the Z vector.  For each such occurrence the dimension of the
       secular equation problem is reduced by one.  This stage is
       performed by the routine SLASD2.

       The second stage consists of calculating the updated
       singular values. This is done by finding the square roots of the
       roots of the secular equation via the routine SLASD4 (as called
       by SLASD3). This routine also calculates the singular vectors of
       the current problem.

       The final stage consists of computing the updated singular vectors
       directly using the updated singular values.  The singular vectors
       for the current problem are multiplied with the singular vectors
       from the overall problem.
    \endverbatim 

 * @param[in] NL
 
          NL is INTEGER
         The row dimension of the upper block.  NL >= 1.
 

 * @param[in] NR
 
          NR is INTEGER
         The row dimension of the lower block.  NR >= 1.
 

 * @param[in] SQRE
 
          SQRE is INTEGER
         = 0: the lower block is an NR-by-NR square matrix.
         = 1: the lower block is an NR-by-(NR+1) rectangular matrix.

         The bidiagonal matrix has row dimension N = NL + NR + 1,
         and column dimension M = N + SQRE.
 

 * @param[in,out] D
 
          D is REAL array, dimension (NL+NR+1).
         N = NL+NR+1
         On entry D(1:NL,1:NL) contains the singular values of the
         upper block; and D(NL+2:N) contains the singular values of
         the lower block. On exit D(1:N) contains the singular values
         of the modified matrix.
 

 * @param[in,out] ALPHA
 
          ALPHA is REAL
         Contains the diagonal element associated with the added row.
 

 * @param[in,out] BETA
 
          BETA is REAL
         Contains the off-diagonal element associated with the added
         row.
 

 * @param[in,out] U
 
          U is REAL array, dimension (LDU,N)
         On entry U(1:NL, 1:NL) contains the left singular vectors of
         the upper block; U(NL+2:N, NL+2:N) contains the left singular
         vectors of the lower block. On exit U contains the left
         singular vectors of the bidiagonal matrix.
 

 * @param[in] LDU
 
          LDU is INTEGER
         The leading dimension of the array U.  LDU >= max(1, N).
 

 * @param[in,out] VT
 
          VT is REAL array, dimension (LDVT,M)
         where M = N + SQRE.
         On entry VT(1:NL+1, 1:NL+1)**T contains the right singular
         vectors of the upper block; VT(NL+2:M, NL+2:M)**T contains
         the right singular vectors of the lower block. On exit
         VT**T contains the right singular vectors of the
         bidiagonal matrix.
 

 * @param[in] LDVT
 
          LDVT is INTEGER
         The leading dimension of the array VT.  LDVT >= max(1, M).
 

 * @param[in,out] IDXQ
 
          IDXQ is INTEGER array, dimension (N)
         This contains the permutation which will reintegrate the
         subproblem just solved back into sorted order, i.e.
         D(IDXQ(I = 1, N)) will be in ascending order.
 

 * @param[out] IWORK
 
          IWORK is INTEGER array, dimension (4*N)
 

 * @param[out] WORK
 
          WORK is REAL array, dimension (3*M**2+2*M)
 

 * @param[out] INFO
 
          INFO is INTEGER
          = 0:  successful exit.
          < 0:  if INFO = -i, the i-th argument had an illegal value.
          > 0:  if INFO = 1, a singular value did not converge
 * */         
template< typename T >
integer lasd1(integer *nl, integer *nr, integer *sqre, T * d, T *alpha, T *beta, T *u, integer *ldu, T *vt, integer *ldvt, integer *idxq, integer *iwork, T *work, integer * info)
{
  return lasd1(nl, nr, sqre, d, alpha, beta, u, ldu, vt, ldvt, idxq, iwork, work, info); 
}

/*! @brief LASD2 merges the two sets of singular values together into a single sorted set. Used by sbdsdc

 * @details
 * \b Purpose:
    \verbatim 
    LASD2 merges the two sets of singular values together into a single
    sorted set.  Then it tries to deflate the size of the problem.
    There are two ways in which deflation can occur:  when two or more
    singular values are close together or if there is a tiny entry in the
    Z vector.  For each such occurrence the order of the related secular
    equation problem is reduced by one.

    SLASD2 is called from SLASD1.
    \endverbatim 

 * @param[in] NL
 
          NL is INTEGER
         The row dimension of the upper block.  NL >= 1.
 

 * @param[in] NR
 
          NR is INTEGER
         The row dimension of the lower block.  NR >= 1.
 

 * @param[in] SQRE
 
          SQRE is INTEGER
         = 0: the lower block is an NR-by-NR square matrix.
         = 1: the lower block is an NR-by-(NR+1) rectangular matrix.

         The bidiagonal matrix has N = NL + NR + 1 rows and
         M = N + SQRE >= N columns.
 

 * @param[out] K
 
          K is INTEGER
         Contains the dimension of the non-deflated matrix,
         This is the order of the related secular equation. 1 <= K <=N.
 

 * @param[in,out] D
 
          D is REAL array, dimension (N)
         On entry D contains the singular values of the two submatrices
         to be combined.  On exit D contains the trailing (N-K) updated
         singular values (those which were deflated) sorted into
         increasing order.
 

 * @param[out] Z
 
          Z is REAL array, dimension (N)
         On exit Z contains the updating row vector in the secular
         equation.
 

 * @param[in] ALPHA
 
          ALPHA is REAL
         Contains the diagonal element associated with the added row.
 

 * @param[in] BETA
 
          BETA is REAL
         Contains the off-diagonal element associated with the added
         row.
 

 * @param[in,out] U
 
          U is REAL array, dimension (LDU,N)
         On entry U contains the left singular vectors of two
         submatrices in the two square blocks with corners at (1,1),
         (NL, NL), and (NL+2, NL+2), (N,N).
         On exit U contains the trailing (N-K) updated left singular
         vectors (those which were deflated) in its last N-K columns.
 

 * @param[in] LDU
 
          LDU is INTEGER
         The leading dimension of the array U.  LDU >= N.
 

 * @param[in,out] VT
 
          VT is REAL array, dimension (LDVT,M)
         On entry VT**T contains the right singular vectors of two
         submatrices in the two square blocks with corners at (1,1),
         (NL+1, NL+1), and (NL+2, NL+2), (M,M).
         On exit VT**T contains the trailing (N-K) updated right singular
         vectors (those which were deflated) in its last N-K columns.
         In case SQRE =1, the last row of VT spans the right null
         space.
 

 * @param[in] LDVT
 
          LDVT is INTEGER
         The leading dimension of the array VT.  LDVT >= M.
 

 * @param[out] DSIGMA
 
          DSIGMA is REAL array, dimension (N)
         Contains a copy of the diagonal elements (K-1 singular values
         and one zero) in the secular equation.
 

 * @param[out] U2
 
          U2 is REAL array, dimension (LDU2,N)
         Contains a copy of the first K-1 left singular vectors which
         will be used by SLASD3 in a matrix multiply (SGEMM) to solve
         for the new left singular vectors. U2 is arranged into four
         blocks. The first block contains a column with 1 at NL+1 and
         zero everywhere else; the second block contains non-zero
         entries only at and above NL; the third contains non-zero
         entries only below NL+1; and the fourth is dense.
 

 * @param[in] LDU2
 
          LDU2 is INTEGER
         The leading dimension of the array U2.  LDU2 >= N.
 

 * @param[out] VT2
 
          VT2 is REAL array, dimension (LDVT2,N)
         VT2**T contains a copy of the first K right singular vectors
         which will be used by SLASD3 in a matrix multiply (SGEMM) to
         solve for the new right singular vectors. VT2 is arranged into
         three blocks. The first block contains a row that corresponds
         to the special 0 diagonal element in SIGMA; the second block
         contains non-zeros only at and before NL +1; the third block
         contains non-zeros only at and after  NL +2.
 

 * @param[in] LDVT2
 
          LDVT2 is INTEGER
         The leading dimension of the array VT2.  LDVT2 >= M.
 

 * @param[out] IDXP
 
          IDXP is INTEGER array, dimension (N)
         This will contain the permutation used to place deflated
         values of D at the end of the array. On output IDXP(2:K)
         points to the nondeflated D-values and IDXP(K+1:N)
         points to the deflated singular values.
 

 * @param[out] IDX
 
          IDX is INTEGER array, dimension (N)
         This will contain the permutation used to sort the contents of
         D into ascending order.
 

 * @param[out] IDXC
 
          IDXC is INTEGER array, dimension (N)
         This will contain the permutation used to arrange the columns
         of the deflated U matrix into three groups:  the first group
         contains non-zero entries only at and above NL, the second
         contains non-zero entries only below NL+2, and the third is
         dense.
 

 * @param[in,out] IDXQ
 
          IDXQ is INTEGER array, dimension (N)
         This contains the permutation which separately sorts the two
         sub-problems in D into ascending order.  Note that entries in
         the first hlaf of this permutation must first be moved one
         position backward; and entries in the second half
         must first have NL+1 added to their values.
 

 * @param[out] COLTYP
 
          COLTYP is INTEGER array, dimension (N)
         As workspace, this will contain a label which will indicate
         which of the following types a column in the U2 matrix or a
         row in the VT2 matrix is:
         1 : non-zero in the upper half only
         2 : non-zero in the lower half only
         3 : dense
         4 : deflated

         On exit, it is an array of dimension 4, with COLTYP(I) being
         the dimension of the I-th type columns.
 

 * @param[out] INFO
 
          INFO is INTEGER
          = 0:  successful exit.
          < 0:  if INFO = -i, the i-th argument had an illegal value. 
 * */         
template< typename T >
integer lasd2(integer *nl, integer *nr, integer *sqre, integer *k, T *d, T *z, T *alpha, T *beta, T *u, integer * ldu, T *vt, integer *ldvt, T *dsigma, T *u2, integer *ldu2, T *vt2, integer *ldvt2, integer *idxp, integer *idx, integer *idxc, integer *idxq, integer *coltyp, integer *info)
{
  return lasd2(nl, nr, sqre, k, d, z, alpha, beta, u, ldu, vt, ldvt, dsigma, u2, ldu2, vt2, ldvt2, idxp, idx, idxc, idxq, coltyp, info); 
}

/*! @brief LASD3 finds all square roots of the roots of the secular equation, \n
     as defined by the values in D and Z, and then updates the singular       \n
     vectors by matrix multiplication. Used by sbdsdc
 * @details
 * \b Purpose:
    \verbatim 
    LASD3 finds all the square roots of the roots of the secular
    equation, as defined by the values in D and Z.  It makes the
    appropriate calls to SLASD4 and then updates the singular
    vectors by matrix multiplication.

    This code makes very mild assumptions about floating point
    arithmetic. It will work on machines with a guard digit in
    add/subtract, or on those binary machines without guard digits
    which subtract like the Cray XMP, Cray YMP, Cray C 90, or Cray 2.
    It could conceivably fail on hexadecimal or decimal machines
    without guard digits, but we know of none.

    SLASD3 is called from SLASD1.
    \endverbatim 


 * @param[in] NL
 
          NL is INTEGER
         The row dimension of the upper block.  NL >= 1.
 

 * @param[in] NR
 
          NR is INTEGER
         The row dimension of the lower block.  NR >= 1.
 

 * @param[in] SQRE
 
          SQRE is INTEGER
         = 0: the lower block is an NR-by-NR square matrix.
         = 1: the lower block is an NR-by-(NR+1) rectangular matrix.

         The bidiagonal matrix has N = NL + NR + 1 rows and
         M = N + SQRE >= N columns.
 

 * @param[in] K
 
          K is INTEGER
         The size of the secular equation, 1 =< K = < N.
 

 * @param[out] D
 
          D is REAL array, dimension(K)
         On exit the square roots of the roots of the secular equation,
         in ascending order.
 

 * @param[out] Q
 
          Q is REAL array, dimension (LDQ,K)
 

 * @param[in] LDQ
 
          LDQ is INTEGER
         The leading dimension of the array Q.  LDQ >= K.
 

 * @param[in,out] DSIGMA
 
          DSIGMA is REAL array, dimension(K)
         The first K elements of this array contain the old roots
         of the deflated updating problem.  These are the poles
         of the secular equation.
 

 * @param[out] U
 
          U is REAL array, dimension (LDU, N)
         The last N - K columns of this matrix contain the deflated
         left singular vectors.
 

 * @param[in] LDU
 
          LDU is INTEGER
         The leading dimension of the array U.  LDU >= N.
 

 * @param[in] U2
 
          U2 is REAL array, dimension (LDU2, N)
         The first K columns of this matrix contain the non-deflated
         left singular vectors for the split problem.
 

 * @param[in] LDU2
 
          LDU2 is INTEGER
         The leading dimension of the array U2.  LDU2 >= N.
 

 * @param[out] VT
 
          VT is REAL array, dimension (LDVT, M)
         The last M - K columns of VT**T contain the deflated
         right singular vectors.
 

 * @param[in] LDVT
 
          LDVT is INTEGER
         The leading dimension of the array VT.  LDVT >= N.
 

 * @param[in,out] VT2
 
          VT2 is REAL array, dimension (LDVT2, N)
         The first K columns of VT2**T contain the non-deflated
         right singular vectors for the split problem.
 

 * @param[in] LDVT2
 
          LDVT2 is INTEGER
         The leading dimension of the array VT2.  LDVT2 >= N.
 

 * @param[in] IDXC
 
          IDXC is INTEGER array, dimension (N)
         The permutation used to arrange the columns of U (and rows of
         VT) into three groups:  the first group contains non-zero
         entries only at and above (or before) NL +1; the second
         contains non-zero entries only at and below (or after) NL+2;
         and the third is dense. The first column of U and the row of
         VT are treated separately, however.

         The rows of the singular vectors found by SLASD4
         must be likewise permuted before the matrix multiplies can
         take place.
 

 * @param[in] CTOT
 
          CTOT is INTEGER array, dimension (4)
         A count of the total number of the various types of columns
         in U (or rows in VT), as described in IDXC. The fourth column
         type is any column which has been deflated.
 

 * @param[in,out] Z
 
          Z is REAL array, dimension (K)
         The first K elements of this array contain the components
         of the deflation-adjusted updating row vector.
 

 * @param[out] INFO
 
          INFO is INTEGER
         = 0:  successful exit.
         < 0:  if INFO = -i, the i-th argument had an illegal value.
         > 0:  if INFO = 1, a singular value did not converge 
 * */         
template< typename T >
integer lasd3(integer *nl, integer *nr, integer *sqre, integer *k, T *d, T *q, integer *ldq, T *dsigma, T *u, integer * ldu, T *u2, integer *ldu2, T *vt, integer *ldvt, T *vt2, integer *ldvt2, integer *idxc, integer *ctot, T *z, integer * info)
{
  return lasd3(nl, nr, sqre, k, d, q, ldq, dsigma, u, ldu, u2, ldu2, vt, ldvt, vt2, ldvt2, idxc, ctot, z, info); 
}

/*! @brief LASD4 computes the square root of the i-th updated eigenvalue  \n
     of a positive symmetric rank-one modification to a positive diagonal \n
     matrix. Used by sbdsdc
 * @details
 * \b Purpose:
    \verbatim 
    This subroutine computes the square root of the I-th updated
    eigenvalue of a positive symmetric rank-one modification to
    a positive diagonal matrix whose entries are given as the squares
    of the corresponding entries in the array d, and that

           0 <= D(i) < D(j)  for  i < j

    and that RHO > 0. This is arranged by the calling routine, and is
    no loss in generality.  The rank-one modified system is thus

           diag(D) * diag(D) +  RHO * Z * Z_transpose.

    where we assume the Euclidean norm of Z is 1.

    The method consists of approximating the rational functions in the
    secular equation by simpler interpolating rational functions.
    \endverbatim 

 * @param[in] N
 
          N is INTEGER
         The length of all arrays.
 

 * @param[in] I
 
          I is INTEGER
         The index of the eigenvalue to be computed.  1 <= I <= N.
 

 * @param[in] D
 
          D is REAL array, dimension (N)
         The original eigenvalues.  It is assumed that they are in
         order, 0 <= D(I) < D(J)  for I < J.
 

 * @param[in] Z
 
          Z is REAL array, dimension (N)
         The components of the updating vector.
 

 * @param[out] DELTA
 
          DELTA is REAL array, dimension (N)
         If N .ne. 1, DELTA contains (D(j) - sigma_I) in its  j-th
         component.  If N = 1, then DELTA(1) = 1.  The vector DELTA
         contains the information necessary to construct the
         (singular) eigenvectors.
 

 * @param[in] RHO
 
          RHO is REAL
         The scalar in the symmetric updating formula.
 

 * @param[out] SIGMA
 
          SIGMA is REAL
         The computed sigma_I, the I-th updated eigenvalue.
 

 * @param[out] WORK
 
          WORK is REAL array, dimension (N)
         If N .ne. 1, WORK contains (D(j) + sigma_I) in its  j-th
         component.  If N = 1, then WORK(1) = 1.
 

 * @param[out] INFO
 
          INFO is INTEGER
         = 0:  successful exit
         > 0:  if INFO = 1, the updating process failed. 
 * */         
template< typename T >
integer lasd4(integer *n, integer *i, T *d, T *z, T *delta, T *rho, T *sigma, T *work, integer *info)
{
  return lasd4(n, i, d, z, delta, rho, sigma, work, info); 
}

/*! @brief LASD5 computes the square root of the i-th eigenvalue of a      \n
     positive symmetric rank-one modification of a 2-by-2 diagonal matrix. \n 
     Used by sbdsdc
 * @details
 * \b Purpose:
    \verbatim 
    This subroutine computes the square root of the I-th eigenvalue
    of a positive symmetric rank-one modification of a 2-by-2 diagonal
    matrix

               diag(D) * diag(D) +  RHO * Z * transpose(Z) .

    The diagonal entries in the array D are assumed to satisfy

               0 <= D(i) < D(j)  for  i < j .

    We also assume RHO > 0 and that the Euclidean norm of the vector
    Z is one.
    \endverbatim 

 * @param[in] I
 
          I is INTEGER
         The index of the eigenvalue to be computed.  I = 1 or I = 2.
 

 * @param[in] D
 
          D is REAL array, dimension (2)
         The original eigenvalues.  We assume 0 <= D(1) < D(2).
 

 * @param[in] Z
 
          Z is REAL array, dimension (2)
         The components of the updating vector.
 

 * @param[out] DELTA
 
          DELTA is REAL array, dimension (2)
         Contains (D(j) - sigma_I) in its  j-th component.
         The vector DELTA contains the information necessary
         to construct the eigenvectors.
 

 * @param[in] RHO
 
          RHO is REAL
         The scalar in the symmetric updating formula.
 

 * @param[out] DSIGMA
 
          DSIGMA is REAL
         The computed sigma_I, the I-th updated eigenvalue.
 

 * @param[out] WORK
 
          WORK is REAL array, dimension (2)
         WORK contains (D(j) + sigma_I) in its  j-th component. 
 * */         
template< typename T >
integer lasd5(integer *i, T *d, T *z, T *delta, T *rho, T *dsigma, T *work)
{
  return lasd5(i, d, z, delta, rho, dsigma, work); 
}

/*! @brief LASD6 computes the SVD of an updated upper bidiagonal matrix \n
     obtained by merging two smaller ones by appending a row.           \n
     Used by sbdsdc
 * @details
 * \b Purpose:
    \verbatim 
    LASD6 computes the SVD of an updated upper bidiagonal matrix B
    obtained by merging two smaller ones by appending a row. This
    routine is used only for the problem which requires all singular
    values and optionally singular vector matrices in factored form.
    B is an N-by-M matrix with N = NL + NR + 1 and M = N + SQRE.
    A related subroutine, SLASD1, handles the case in which all singular
    values and singular vectors of the bidiagonal matrix are desired.

    SLASD6 computes the SVD as follows:

                  (D1(in)    0    0       0)
      B = U(in) * (  Z1**T   a   Z2**T    b) * VT(in)
                  (  0       0   D2(in)   0)

        = U(out) * (D(out) 0) * VT(out)

    where Z**T = (Z1**T a Z2**T b) = u**T VT**T, and u is a vector of dimension M
    with ALPHA and BETA in the NL+1 and NL+2 th entries and zeros
    elsewhere; and the entry b is empty if SQRE = 0.

    The singular values of B can be computed using D1, D2, the first
    components of all the right singular vectors of the lower block, and
    the last components of all the right singular vectors of the upper
    block. These components are stored and updated in VF and VL,
    respectively, in SLASD6. Hence U and VT are not explicitly
    referenced.

    The singular values are stored in D. The algorithm consists of two
    stages:

          The first stage consists of deflating the size of the problem
          when there are multiple singular values or if there is a zero
          in the Z vector. For each such occurrence the dimension of the
          secular equation problem is reduced by one. This stage is
          performed by the routine SLASD7.

          The second stage consists of calculating the updated
          singular values. This is done by finding the roots of the
          secular equation via the routine SLASD4 (as called by SLASD8).
          This routine also updates VF and VL and computes the distances
          between the updated singular values and the old singular
          values.

    SLASD6 is called from SLASDA.
    \endverbatim 

 * @param[in] ICOMPQ
 
          ICOMPQ is INTEGER
         Specifies whether singular vectors are to be computed in
         factored form:
         = 0: Compute singular values only.
         = 1: Compute singular vectors in factored form as well.
 

 * @param[in] NL
 
          NL is INTEGER
         The row dimension of the upper block.  NL >= 1.
 

 * @param[in] NR
 
          NR is INTEGER
         The row dimension of the lower block.  NR >= 1.
 

 * @param[in] SQRE
 
          SQRE is INTEGER
         = 0: the lower block is an NR-by-NR square matrix.
         = 1: the lower block is an NR-by-(NR+1) rectangular matrix.

         The bidiagonal matrix has row dimension N = NL + NR + 1,
         and column dimension M = N + SQRE.
 

 * @param[in,out] D
 
          D is REAL array, dimension (NL+NR+1).
         On entry D(1:NL,1:NL) contains the singular values of the
         upper block, and D(NL+2:N) contains the singular values
         of the lower block. On exit D(1:N) contains the singular
         values of the modified matrix.
 

 * @param[in,out] VF
 
          VF is REAL array, dimension (M)
         On entry, VF(1:NL+1) contains the first components of all
         right singular vectors of the upper block; and VF(NL+2:M)
         contains the first components of all right singular vectors
         of the lower block. On exit, VF contains the first components
         of all right singular vectors of the bidiagonal matrix.
 

 * @param[in,out] VL
 
          VL is REAL array, dimension (M)
         On entry, VL(1:NL+1) contains the  last components of all
         right singular vectors of the upper block; and VL(NL+2:M)
         contains the last components of all right singular vectors of
         the lower block. On exit, VL contains the last components of
         all right singular vectors of the bidiagonal matrix.
 

 * @param[in,out] ALPHA
 
          ALPHA is REAL
         Contains the diagonal element associated with the added row.
 

 * @param[in,out] BETA
 
          BETA is REAL
         Contains the off-diagonal element associated with the added
         row.
 

 * @param[in,out] IDXQ
 
          IDXQ is INTEGER array, dimension (N)
         This contains the permutation which will reintegrate the
         subproblem just solved back into sorted order, i.e.
         D(IDXQ(I = 1, N)) will be in ascending order.
 

 * @param[out] PERM
 
          PERM is INTEGER array, dimension (N)
         The permutations (from deflation and sorting) to be applied
         to each block. Not referenced if ICOMPQ = 0.
 

 * @param[out] GIVPTR
 
          GIVPTR is INTEGER
         The number of Givens rotations which took place in this
         subproblem. Not referenced if ICOMPQ = 0.
 

 * @param[out] GIVCOL
 
          GIVCOL is INTEGER array, dimension (LDGCOL, 2)
         Each pair of numbers indicates a pair of columns to take place
         in a Givens rotation. Not referenced if ICOMPQ = 0.
 

 * @param[in] LDGCOL
 
          LDGCOL is INTEGER
         leading dimension of GIVCOL, must be at least N.
 

 * @param[out] GIVNUM
 
          GIVNUM is REAL array, dimension (LDGNUM, 2)
         Each number indicates the C or S value to be used in the
         corresponding Givens rotation. Not referenced if ICOMPQ = 0.
 

 * @param[in] LDGNUM
 
          LDGNUM is INTEGER
         The leading dimension of GIVNUM and POLES, must be at least N.
 

 * @param[out] POLES
 
          POLES is REAL array, dimension (LDGNUM, 2)
         On exit, POLES(1,*) is an array containing the new singular
         values obtained from solving the secular equation, and
         POLES(2,*) is an array containing the poles in the secular
         equation. Not referenced if ICOMPQ = 0.
 

 * @param[out] DIFL
 
          DIFL is REAL array, dimension (N)
         On exit, DIFL(I) is the distance between I-th updated
         (undeflated) singular value and the I-th (undeflated) old
         singular value.
 

 * @param[out] DIFR
 
          DIFR is REAL array,
                   dimension (LDDIFR, 2) if ICOMPQ = 1 and
                   dimension (K) if ICOMPQ = 0.
          On exit, DIFR(I,1) = D(I) - DSIGMA(I+1), DIFR(K,1) is not
          defined and will not be referenced.

          If ICOMPQ = 1, DIFR(1:K,2) is an array containing the
          normalizing factors for the right singular vector matrix.

         See SLASD8 for details on DIFL and DIFR.
 

 * @param[out] Z
 
          Z is REAL array, dimension (M)
         The first elements of this array contain the components
         of the deflation-adjusted updating row vector.
 

 * @param[out] K
 
          K is INTEGER
         Contains the dimension of the non-deflated matrix,
         This is the order of the related secular equation. 1 <= K <=N.
 

 * @param[out] C
 
          C is REAL
         C contains garbage if SQRE =0 and the C-value of a Givens
         rotation related to the right null space if SQRE = 1.
 

 * @param[out] S
 
          S is REAL
         S contains garbage if SQRE =0 and the S-value of a Givens
         rotation related to the right null space if SQRE = 1.
 

 * @param[out] WORK
 
          WORK is REAL array, dimension (4 * M)
 

 * @param[out] IWORK
 
          IWORK is INTEGER array, dimension (3 * N)
 

 * @param[out] INFO
 
          INFO is INTEGER
          = 0:  successful exit.
          < 0:  if INFO = -i, the i-th argument had an illegal value.
          > 0:  if INFO = 1, a singular value did not converge 
 * */         
template< typename T >
integer lasd6(integer *icompq, integer *nl, integer *nr, integer *sqre, T *d, T *vf, T *vl, T *alpha, T *beta, integer *idxq, integer *perm, integer *givptr, integer *givcol, integer *ldgcol, T *givnum, integer *ldgnum, T *poles, T * difl, T *difr, T *z, integer *k, T *c, T *s, T * work, integer *iwork, integer *info)
{
  return lasd6(icompq, nl, nr, sqre, d, vf, vl, alpha, beta, idxq, perm, givptr, givcol, ldgcol, givnum, ldgnum, poles, difl, difr, z, k, c, s, work, iwork, info); 
}

/*! @brief LASD7 merges the two sets of singular values together into a   \n
     single sorted set. Then it tries to deflate the size of the problem. \n
     Used by sbdsdc
 * @details
 * \b Purpose:
    \verbatim 
    LASD7 merges the two sets of singular values together into a single
    sorted set. Then it tries to deflate the size of the problem. There
    are two ways in which deflation can occur:  when two or more singular
    values are close together or if there is a tiny entry in the Z
    vector. For each such occurrence the order of the related
    secular equation problem is reduced by one.

    SLASD7 is called from SLASD6.
    \endverbatim  

 * @param[in] ICOMPQ
 
          ICOMPQ is INTEGER
          Specifies whether singular vectors are to be computed
          in compact form, as follows:
          = 0: Compute singular values only.
          = 1: Compute singular vectors of upper
               bidiagonal matrix in compact form.
 

 * @param[in] NL
 
          NL is INTEGER
         The row dimension of the upper block. NL >= 1.
 

 * @param[in] NR
 
          NR is INTEGER
         The row dimension of the lower block. NR >= 1.
 

 * @param[in] SQRE
 
          SQRE is INTEGER
         = 0: the lower block is an NR-by-NR square matrix.
         = 1: the lower block is an NR-by-(NR+1) rectangular matrix.

         The bidiagonal matrix has
         N = NL + NR + 1 rows and
         M = N + SQRE >= N columns.
 

 * @param[out] K
 
          K is INTEGER
         Contains the dimension of the non-deflated matrix, this is
         the order of the related secular equation. 1 <= K <=N.
 

 * @param[in,out] D
 
          D is REAL array, dimension (N)
         On entry D contains the singular values of the two submatrices
         to be combined. On exit D contains the trailing (N-K) updated
         singular values (those which were deflated) sorted into
         increasing order.
 

 * @param[out] Z
 
          Z is REAL array, dimension (M)
         On exit Z contains the updating row vector in the secular
         equation.
 

 * @param[out] ZW
 
          ZW is REAL array, dimension (M)
         Workspace for Z.
 

 * @param[in,out] VF
 
          VF is REAL array, dimension (M)
         On entry, VF(1:NL+1) contains the first components of all
         right singular vectors of the upper block; and VF(NL+2:M)
         contains the first components of all right singular vectors
         of the lower block. On exit, VF contains the first components
         of all right singular vectors of the bidiagonal matrix.
 

 * @param[out] VFW
 
          VFW is REAL array, dimension (M)
         Workspace for VF.
 

 * @param[in,out] VL
 
          VL is REAL array, dimension (M)
         On entry, VL(1:NL+1) contains the  last components of all
         right singular vectors of the upper block; and VL(NL+2:M)
         contains the last components of all right singular vectors
         of the lower block. On exit, VL contains the last components
         of all right singular vectors of the bidiagonal matrix.
 

 * @param[out] VLW
 
          VLW is REAL array, dimension (M)
         Workspace for VL.
 

 * @param[in] ALPHA
 
          ALPHA is REAL
         Contains the diagonal element associated with the added row.
 

 * @param[in] BETA
 
          BETA is REAL
         Contains the off-diagonal element associated with the added
         row.
 

 * @param[out] DSIGMA
 
          DSIGMA is REAL array, dimension (N)
         Contains a copy of the diagonal elements (K-1 singular values
         and one zero) in the secular equation.
 

 * @param[out] IDX
 
          IDX is INTEGER array, dimension (N)
         This will contain the permutation used to sort the contents of
         D into ascending order.
 

 * @param[out] IDXP
 
          IDXP is INTEGER array, dimension (N)
         This will contain the permutation used to place deflated
         values of D at the end of the array. On output IDXP(2:K)
         points to the nondeflated D-values and IDXP(K+1:N)
         points to the deflated singular values.
 

 * @param[in] IDXQ
 
          IDXQ is INTEGER array, dimension (N)
         This contains the permutation which separately sorts the two
         sub-problems in D into ascending order.  Note that entries in
         the first half of this permutation must first be moved one
         position backward; and entries in the second half
         must first have NL+1 added to their values.
 

 * @param[out] PERM
 
          PERM is INTEGER array, dimension (N)
         The permutations (from deflation and sorting) to be applied
         to each singular block. Not referenced if ICOMPQ = 0.
 

 * @param[out] GIVPTR
 
          GIVPTR is INTEGER
         The number of Givens rotations which took place in this
         subproblem. Not referenced if ICOMPQ = 0.
 

 * @param[out] GIVCOL
 
          GIVCOL is INTEGER array, dimension (LDGCOL, 2)
         Each pair of numbers indicates a pair of columns to take place
         in a Givens rotation. Not referenced if ICOMPQ = 0.
 

 * @param[in] LDGCOL
 
          LDGCOL is INTEGER
         The leading dimension of GIVCOL, must be at least N.
 

 * @param[out] GIVNUM
 
          GIVNUM is REAL array, dimension (LDGNUM, 2)
         Each number indicates the C or S value to be used in the
         corresponding Givens rotation. Not referenced if ICOMPQ = 0.
 

 * @param[in] LDGNUM
 
          LDGNUM is INTEGER
         The leading dimension of GIVNUM, must be at least N.
 

 * @param[out] C
 
          C is REAL
         C contains garbage if SQRE =0 and the C-value of a Givens
         rotation related to the right null space if SQRE = 1.
 

 * @param[out] S
 
          S is REAL
         S contains garbage if SQRE =0 and the S-value of a Givens
         rotation related to the right null space if SQRE = 1.
 

 * @param[out] INFO
 
          INFO is INTEGER
         = 0:  successful exit.
         < 0:  if INFO = -i, the i-th argument had an illegal value. 
 * */         
template< typename T >
integer lasd7(integer *icompq, integer *nl, integer *nr, integer *sqre, integer *k, T *d, T *z, T *zw, T *vf, T *vfw, T *vl, T *vlw, T *alpha, T *beta, T *dsigma, integer *idx, integer *idxp, integer *idxq, integer *perm, integer * givptr, integer *givcol, integer *ldgcol, T *givnum, integer * ldgnum, T *c, T *s, integer *info)
{
  return lasd7(icompq, nl, nr, sqre, k, d, z, zw, vf, vfw, vl, vlw, alpha, beta, dsigma, idx, idxp, idxq, perm, givptr, givcol, ldgcol, givnum, ldgnum, c, s, info); 
}

/*! @brief LASD8 finds the square roots of the roots of the secular equation, \n
     and stores, for each element in D, the distance to its two nearest poles. \n
     Used by sbdsdc
 * @details
 * \b Purpose:
    \verbatim 
    LASD8 finds the square roots of the roots of the secular equation,
    as defined by the values in DSIGMA and Z. It makes the appropriate
    calls to SLASD4, and stores, for each  element in D, the distance
    to its two nearest poles (elements in DSIGMA). It also updates
    the arrays VF and VL, the first and last components of all the
    right singular vectors of the original bidiagonal matrix.

    LASD8 is called from LASD6.
    \endverbatim 

 * @param[in] ICOMPQ
 
          ICOMPQ is INTEGER
          Specifies whether singular vectors are to be computed in
          factored form in the calling routine:
          = 0: Compute singular values only.
          = 1: Compute singular vectors in factored form as well.
 

 * @param[in] K
 
          K is INTEGER
          The number of terms in the rational function to be solved
          by SLASD4.  K >= 1.
 

 * @param[out] D
 
          D is REAL array, dimension (K)
          On output, D contains the updated singular values.
 

 * @param[in,out] Z
 
          Z is REAL array, dimension (K)
          On entry, the first K elements of this array contain the
          components of the deflation-adjusted updating row vector.
          On exit, Z is updated.
 

 * @param[in,out] VF
 
          VF is REAL array, dimension (K)
          On entry, VF contains  information passed through DBEDE8.
          On exit, VF contains the first K components of the first
          components of all right singular vectors of the bidiagonal
          matrix.
 

 * @param[in,out] VL
 
          VL is REAL array, dimension (K)
          On entry, VL contains  information passed through DBEDE8.
          On exit, VL contains the first K components of the last
          components of all right singular vectors of the bidiagonal
          matrix.
 

 * @param[out] DIFL
 
          DIFL is REAL array, dimension (K)
          On exit, DIFL(I) = D(I) - DSIGMA(I).
 

 * @param[out] DIFR
 
          DIFR is REAL array,
                   dimension (LDDIFR, 2) if ICOMPQ = 1 and
                   dimension (K) if ICOMPQ = 0.
          On exit, DIFR(I,1) = D(I) - DSIGMA(I+1), DIFR(K,1) is not
          defined and will not be referenced.

          If ICOMPQ = 1, DIFR(1:K,2) is an array containing the
          normalizing factors for the right singular vector matrix.
 

 * @param[in] LDDIFR
 
          LDDIFR is INTEGER
          The leading dimension of DIFR, must be at least K.
 

 * @param[in,out] DSIGMA
 
          DSIGMA is REAL array, dimension (K)
          On entry, the first K elements of this array contain the old
          roots of the deflated updating problem.  These are the poles
          of the secular equation.
          On exit, the elements of DSIGMA may be very slightly altered
          in value.
 

 * @param[out] WORK
 
          WORK is REAL array, dimension (3*K)
 

 * @param[out] INFO
 
          INFO is INTEGER
          = 0:  successful exit.
          < 0:  if INFO = -i, the i-th argument had an illegal value.
          > 0:  if INFO = 1, a singular value did not converge 
 * */         
template< typename T >
integer lasd8(integer *icompq, integer *k, T *d, T * z, T *vf, T *vl, T *difl, T *difr, integer *lddifr, T *dsigma, T *work, integer *info)
{
  return lasd8(icompq, k, d, z, vf, vl, difl, difr, lddifr, dsigma, work, info); 
}


/*! @brief LASDA computes the singular value decomposition (SVD) of a \n
     real upper bidiagonal matrix with diagonal d and off-diagonal e. \n
     Used by sbdsdc
 * @details
 * \b Purpose:
    \verbatim 
    Using a divide and conquer approach, LASDA computes the singular
    value decomposition (SVD) of a real upper bidiagonal N-by-M matrix
    B with diagonal D and offdiagonal E, where M = N + SQRE. The
    algorithm computes the singular values in the SVD B = U * S * VT.
    The orthogonal matrices U and VT are optionally computed in
    compact form.

    A related subroutine, SLASD0, computes the singular values and
    the singular vectors in explicit form.
    \endverbatim 

 * @param[in] ICOMPQ
 
          ICOMPQ is INTEGER
         Specifies whether singular vectors are to be computed
         in compact form, as follows
         = 0: Compute singular values only.
         = 1: Compute singular vectors of upper bidiagonal
              matrix in compact form.
 

 * @param[in] SMLSIZ
 
          SMLSIZ is INTEGER
         The maximum size of the subproblems at the bottom of the
         computation tree.
 

 * @param[in] N
 
          N is INTEGER
         The row dimension of the upper bidiagonal matrix. This is
         also the dimension of the main diagonal array D.
 

 * @param[in] SQRE
 
          SQRE is INTEGER
         Specifies the column dimension of the bidiagonal matrix.
         = 0: The bidiagonal matrix has column dimension M = N;
         = 1: The bidiagonal matrix has column dimension M = N + 1.
 

 * @param[in,out] D
 
          D is REAL array, dimension (N)
         On entry D contains the main diagonal of the bidiagonal
         matrix. On exit D, if INFO = 0, contains its singular values.
 

 * @param[in] E
 
          E is REAL array, dimension (M-1)
         Contains the subdiagonal entries of the bidiagonal matrix.
         On exit, E has been destroyed.
 

 * @param[out] U
 
          U is REAL array,
         dimension (LDU, SMLSIZ) if ICOMPQ = 1, and not referenced
         if ICOMPQ = 0. If ICOMPQ = 1, on exit, U contains the left
         singular vector matrices of all subproblems at the bottom
         level.
 

 * @param[in] LDU
 
          LDU is INTEGER, LDU = > N.
         The leading dimension of arrays U, VT, DIFL, DIFR, POLES,
         GIVNUM, and Z.
 

 * @param[out] VT
 
          VT is REAL array,
         dimension (LDU, SMLSIZ+1) if ICOMPQ = 1, and not referenced
         if ICOMPQ = 0. If ICOMPQ = 1, on exit, VT**T contains the right
         singular vector matrices of all subproblems at the bottom
         level.
 

 * @param[out] K
 
          K is INTEGER array, dimension (N)
         if ICOMPQ = 1 and dimension 1 if ICOMPQ = 0.
         If ICOMPQ = 1, on exit, K(I) is the dimension of the I-th
         secular equation on the computation tree.
 

 * @param[out] DIFL
 
          DIFL is REAL array, dimension (LDU, NLVL),
         where NLVL = floor(log_2 (N/SMLSIZ))).
 

 * @param[out] DIFR
 
          DIFR is REAL array,
                  dimension (LDU, 2 * NLVL) if ICOMPQ = 1 and
                  dimension (N) if ICOMPQ = 0.
         If ICOMPQ = 1, on exit, DIFL(1:N, I) and DIFR(1:N, 2 * I - 1)
         record distances between singular values on the I-th
         level and singular values on the (I -1)-th level, and
         DIFR(1:N, 2 * I) contains the normalizing factors for
         the right singular vector matrix. See SLASD8 for details.
 

 * @param[out] Z
 
          Z is REAL array,
                  dimension (LDU, NLVL) if ICOMPQ = 1 and
                  dimension (N) if ICOMPQ = 0.
         The first K elements of Z(1, I) contain the components of
         the deflation-adjusted updating row vector for subproblems
         on the I-th level.
 

 * @param[out] POLES
 
          POLES is REAL array,
         dimension (LDU, 2 * NLVL) if ICOMPQ = 1, and not referenced
         if ICOMPQ = 0. If ICOMPQ = 1, on exit, POLES(1, 2*I - 1) and
         POLES(1, 2*I) contain  the new and old singular values
         involved in the secular equations on the I-th level.
 

 * @param[out] GIVPTR
 
          GIVPTR is INTEGER array,
         dimension (N) if ICOMPQ = 1, and not referenced if
         ICOMPQ = 0. If ICOMPQ = 1, on exit, GIVPTR(I) records
         the number of Givens rotations performed on the I-th
         problem on the computation tree.
 

 * @param[out] GIVCOL
 
          GIVCOL is INTEGER array,
         dimension (LDGCOL, 2 * NLVL) if ICOMPQ = 1, and not
         referenced if ICOMPQ = 0. If ICOMPQ = 1, on exit, for each I,
         GIVCOL(1, 2 *I - 1) and GIVCOL(1, 2 *I) record the locations
         of Givens rotations performed on the I-th level on the
         computation tree.
 

 * @param[in] LDGCOL
 
          LDGCOL is INTEGER, LDGCOL = > N.
         The leading dimension of arrays GIVCOL and PERM.
 

 * @param[out] PERM
 
          PERM is INTEGER array, dimension (LDGCOL, NLVL)
         if ICOMPQ = 1, and not referenced
         if ICOMPQ = 0. If ICOMPQ = 1, on exit, PERM(1, I) records
         permutations done on the I-th level of the computation tree.
 

 * @param[out] GIVNUM
 
          GIVNUM is REAL array,
         dimension (LDU, 2 * NLVL) if ICOMPQ = 1, and not
         referenced if ICOMPQ = 0. If ICOMPQ = 1, on exit, for each I,
         GIVNUM(1, 2 *I - 1) and GIVNUM(1, 2 *I) record the C- and S-
         values of Givens rotations performed on the I-th level on
         the computation tree.
 

 * @param[out] C
 
          C is REAL array,
         dimension (N) if ICOMPQ = 1, and dimension 1 if ICOMPQ = 0.
         If ICOMPQ = 1 and the I-th subproblem is not square, on exit,
         C(I) contains the C-value of a Givens rotation related to
         the right null space of the I-th subproblem.
 

 * @param[out] S
 
          S is REAL array, dimension (N) if
         ICOMPQ = 1, and dimension 1 if ICOMPQ = 0. If ICOMPQ = 1
         and the I-th subproblem is not square, on exit, S(I)
         contains the S-value of a Givens rotation related to
         the right null space of the I-th subproblem.
 

 * @param[out] WORK
 
          WORK is REAL array, dimension
         (6 * N + (SMLSIZ + 1)*(SMLSIZ + 1)).
 

 * @param[out] IWORK
 
          IWORK is INTEGER array, dimension (7*N).
 

 * @param[out] INFO
 
          INFO is INTEGER
          = 0:  successful exit.
          < 0:  if INFO = -i, the i-th argument had an illegal value.
          > 0:  if INFO = 1, a singular value did not converge 
 * */         
template< typename T >
integer lasda(integer *icompq, integer *smlsiz, integer *n, integer *sqre, T *d, T *e, T *u, integer *ldu, T *vt, integer *k, T *difl, T *difr, T *z, T *poles, integer * givptr, integer *givcol, integer *ldgcol, integer *perm, T *givnum, T *c, T *s, T *work, integer *iwork, integer *info)
{
  return lasda(icompq, smlsiz, n, sqre, d, e, u, ldu, vt, k, difl, difr, z, poles, givptr, givcol, ldgcol, perm, givnum, c, s, work, iwork, info); 
}

/*! @brief LASDQ computes the SVD of a real bidiagonal matrix with diagonal \n
     d and off-diagonal e. Used by sbdsdc
 * @details
 * \b Purpose:
    \verbatim 
    LASDQ computes the singular value decomposition (SVD) of a real
    (upper or lower) bidiagonal matrix with diagonal D and offdiagonal
    E, accumulating the transformations if desired. Letting B denote
    the input bidiagonal matrix, the algorithm computes orthogonal
    matrices Q and P such that B = Q * S * P**T (P**T denotes the transpose
    of P). The singular values S are overwritten on D.

    The input matrix U  is changed to U  * Q  if desired.
    The input matrix VT is changed to P**T * VT if desired.
    The input matrix C  is changed to Q**T * C  if desired.

    See "Computing  Small Singular Values of Bidiagonal Matrices With
    Guaranteed High Relative Accuracy," by J. Demmel and W. Kahan,
    LAPACK Working Note #3, for a detailed description of the algorithm.
    \endverbatim 

 * @param[in] UPLO
 
          UPLO is CHARACTER*1
        On entry, UPLO specifies whether the input bidiagonal matrix
        is upper or lower bidiagonal, and whether it is square are
        not.
           UPLO = 'U' or 'u'   B is upper bidiagonal.
           UPLO = 'L' or 'l'   B is lower bidiagonal.
 

 * @param[in] SQRE
 
          SQRE is INTEGER
        = 0: then the input matrix is N-by-N.
        = 1: then the input matrix is N-by-(N+1) if UPLU = 'U' and
             (N+1)-by-N if UPLU = 'L'.

        The bidiagonal matrix has
        N = NL + NR + 1 rows and
        M = N + SQRE >= N columns.
 

 * @param[in] N
 
          N is INTEGER
        On entry, N specifies the number of rows and columns
        in the matrix. N must be at least 0.
 

 * @param[in] NCVT
 
          NCVT is INTEGER
        On entry, NCVT specifies the number of columns of
        the matrix VT. NCVT must be at least 0.
 

 * @param[in] NRU
 
          NRU is INTEGER
        On entry, NRU specifies the number of rows of
        the matrix U. NRU must be at least 0.
 

 * @param[in] NCC
 
          NCC is INTEGER
        On entry, NCC specifies the number of columns of
        the matrix C. NCC must be at least 0.
 

 * @param[in,out] D
 
          D is REAL array, dimension (N)
        On entry, D contains the diagonal entries of the
        bidiagonal matrix whose SVD is desired. On normal exit,
        D contains the singular values in ascending order.
 

 * @param[in,out] E
 
          E is REAL array.
        dimension is (N-1) if SQRE = 0 and N if SQRE = 1.
        On entry, the entries of E contain the offdiagonal entries
        of the bidiagonal matrix whose SVD is desired. On normal
        exit, E will contain 0. If the algorithm does not converge,
        D and E will contain the diagonal and superdiagonal entries
        of a bidiagonal matrix orthogonally equivalent to the one
        given as input.
 

 * @param[in,out] VT
 
          VT is REAL array, dimension (LDVT, NCVT)
        On entry, contains a matrix which on exit has been
        premultiplied by P**T, dimension N-by-NCVT if SQRE = 0
        and (N+1)-by-NCVT if SQRE = 1 (not referenced if NCVT=0).
 

 * @param[in] LDVT
 
          LDVT is INTEGER
        On entry, LDVT specifies the leading dimension of VT as
        declared in the calling (sub) program. LDVT must be at
        least 1. If NCVT is nonzero LDVT must also be at least N.
 

 * @param[in,out] U
 
          U is REAL array, dimension (LDU, N)
        On entry, contains a  matrix which on exit has been
        postmultiplied by Q, dimension NRU-by-N if SQRE = 0
        and NRU-by-(N+1) if SQRE = 1 (not referenced if NRU=0).
 

 * @param[in] LDU
 
          LDU is INTEGER
        On entry, LDU  specifies the leading dimension of U as
        declared in the calling (sub) program. LDU must be at
        least max(1, NRU) .
 

 * @param[in,out] C
 
          C is REAL array, dimension (LDC, NCC)
        On entry, contains an N-by-NCC matrix which on exit
        has been premultiplied by Q**T  dimension N-by-NCC if SQRE = 0
        and (N+1)-by-NCC if SQRE = 1 (not referenced if NCC=0).
 

 * @param[in] LDC
 
          LDC is INTEGER
        On entry, LDC  specifies the leading dimension of C as
        declared in the calling (sub) program. LDC must be at
        least 1. If NCC is nonzero, LDC must also be at least N.
 

 * @param[out] WORK
 
          WORK is REAL array, dimension (4*N)
        Workspace. Only referenced if one of NCVT, NRU, or NCC is
        nonzero, and if N is at least 2.
 

 * @param[out] INFO
 
          INFO is INTEGER
        On exit, a value of 0 indicates a successful exit.
        If INFO < 0, argument number -INFO is illegal.
        If INFO > 0, the algorithm did not converge, and INFO
        specifies how many superdiagonals did not converge. 
 * */         
template< typename T >
integer lasdq(char *uplo, integer *sqre, integer *n, integer * ncvt, integer *nru, integer *ncc, T *d, T *e, T *vt, integer *ldvt, T *u, integer *ldu, T *c, integer *ldc, T * work, integer *info)
{
  return lasdq(uplo, sqre, n, ncvt, nru, ncc, d, e, vt, ldvt, u, ldu, c, ldc, work, info); 
}

/*! @brief LASDT creates a tree of subproblems for bidiagonal divide and conquer. \n
     Used by sbdsdc
 * @details
 * \b Purpose:
    \verbatim 
    LASDT creates a tree of subproblems for bidiagonal divide and
    conquer.
    \endverbatim  

 * @param[in] N
 
          N is INTEGER
          On entry, the number of diagonal elements of the
          bidiagonal matrix.
 

 * @param[out] LVL
 
          LVL is INTEGER
          On exit, the number of levels on the computation tree.
 

 * @param[out] ND
 
          ND is INTEGER
          On exit, the number of nodes on the tree.
 

 * @param[out] INODE
 
          INODE is INTEGER array, dimension (N)
          On exit, centers of subproblems.
 

 * @param[out] NDIML
 
          NDIML is INTEGER array, dimension (N)
          On exit, row dimensions of left children.
 

 * @param[out] NDIMR
 
          NDIMR is INTEGER array, dimension (N)
          On exit, row dimensions of right children.
 

 * @param[in] MSUB
 
          MSUB is INTEGER
          On entry, the maximum row dimension each subproblem at the
          bottom of the tree can be of. 
 * */         
integer slasdt(integer *n, integer *lvl, integer *nd, integer * inode, integer *ndiml, integer *ndimr, integer *msub)
{
  return slasdt_(n, lvl, nd, inode, ndiml, ndimr, msub); 
}
integer dlasdt(integer *n, integer *lvl, integer *nd, integer * inode, integer *ndiml, integer *ndimr, integer *msub)
{
  return dlasdt_(n, lvl, nd, inode, ndiml, ndimr, msub); 
}

/*! @brief LASQ1 computes the singular values of a real square bidiagonal matrix. \n
     Used by sbdsqr
 * @details
 * \b Purpose:
    \verbatim 
    LASQ1 computes the singular values of a real N-by-N bidiagonal
    matrix with diagonal D and off-diagonal E. The singular values
    are computed to high relative accuracy, in the absence of
    denormalization, underflow and overflow. The algorithm was first
    presented in

    "Accurate singular values and differential qd algorithms" by K. V.
    Fernando and B. N. Parlett, Numer. Math., Vol-67, No. 2, pp. 191-230,
    1994,

    and the present implementation is described in "An implementation of
    the dqds Algorithm (Positive Case)", LAPACK Working Note.
    \endverbatim 

 * @param[in] N
 
          N is INTEGER
        The number of rows and columns in the matrix. N >= 0.
 

 * @param[in,out] D
 
          D is REAL array, dimension (N)
        On entry, D contains the diagonal elements of the
        bidiagonal matrix whose SVD is desired. On normal exit,
        D contains the singular values in decreasing order.
 

 * @param[in,out] E
 
          E is REAL array, dimension (N)
        On entry, elements E(1:N-1) contain the off-diagonal elements
        of the bidiagonal matrix whose SVD is desired.
        On exit, E is overwritten.
 

 * @param[out] WORK
 
          WORK is REAL array, dimension (4*N)
 

 * @param[out] INFO
 
          INFO is INTEGER
        = 0: successful exit
        < 0: if INFO = -i, the i-th argument had an illegal value
        > 0: the algorithm failed
             = 1, a split was marked by a positive value in E
             = 2, current block of Z not diagonalized after 100*N
                  iterations (in inner while loop)  On exit D and E
                  represent a matrix with the same singular values
                  which the calling subroutine could use to finish the
                  computation, or even feed back into SLASQ1
             = 3, termination criterion of outer while loop not met
                  (program created more than N unreduced blocks)
 * */         
template< typename T >
integer lasq1(integer *n, T *d, T *e, T *work, integer *info)
{
  return lasq1(n, d, e, work, info);
}

/*! @brief LASQ2 computes all the eigenvalues of the symmetric positive definite   \n
     tridiagonal matrix associated with the qd Array Z to high relative accuracy.  \n
     Used by sbdsqr and sstegr.
 * @details
 * \b Purpose:
    \verbatim 
    LASQ2 computes all the eigenvalues of the symmetric positive
    definite tridiagonal matrix associated with the qd array Z to high
    relative accuracy are computed to high relative accuracy, in the
    absence of denormalization, underflow and overflow.

    To see the relation of Z to the tridiagonal matrix, let L be a
    unit lower bidiagonal matrix with subdiagonals Z(2,4,6,,..) and
    let U be an upper bidiagonal matrix with 1's above and diagonal
    Z(1,3,5,,..). The tridiagonal is L*U or, if you prefer, the
    symmetric tridiagonal to which it is similar.

    Note : SLASQ2 defines a logical variable, IEEE, which is true
    on machines which follow ieee-754 floating-point standard in their
    handling of infinities and NaNs, and false otherwise. This variable
    is passed to SLASQ3.
    \endverbatim 


 * @param[in] N
 
          N is INTEGER
        The number of rows and columns in the matrix. N >= 0.
 

 * @param[in,out] Z
 
          Z is REAL array, dimension (4*N)
        On entry Z holds the qd array. On exit, entries 1 to N hold
        the eigenvalues in decreasing order, Z(2*N+1) holds the
        trace, and Z(2*N+2) holds the sum of the eigenvalues. If
        N > 2, then Z(2*N+3) holds the iteration count, Z(2*N+4)
        holds NDIVS/NIN^2, and Z(2*N+5) holds the percentage of
        shifts that failed.
 

 * @param[out] INFO
 
          INFO is INTEGER
        = 0: successful exit
        < 0: if the i-th argument is a scalar and had an illegal
             value, then INFO = -i, if the i-th argument is an
             array and the j-entry had an illegal value, then
             INFO = -(i*100+j)
        > 0: the algorithm failed
              = 1, a split was marked by a positive value in E
              = 2, current block of Z not diagonalized after 100*N
                   iterations (in inner while loop).  On exit Z holds
                   a qd array with the same eigenvalues as the given Z.
              = 3, termination criterion of outer while loop not met
                   (program created more than N unreduced blocks) 
 * */         
template< typename T >
integer lasq2(integer *n, T *z, integer *info)
{
  return lasq2(n, z, info);
}

/*! @brief LASQ3 checks for deflation, computes a shift and calls dqds. Used by sbdsqr.

 * @details
 * \b Purpose:
    \verbatim 
    LASQ3 checks for deflation, computes a shift (TAU) and calls dqds.
    In case of failure it changes shifts, and tries again until output
    is positive.
    \endverbatim 

 * @param[in] I0
 
          I0 is INTEGER
         First index.
 

 * @param[in,out] N0
 
          N0 is INTEGER
         Last index.
 

 * @param[in,out] Z
 
          Z is REAL array, dimension (4*N0)
         Z holds the qd array.
 

 * @param[in,out] PP
 
          PP is INTEGER
         PP=0 for ping, PP=1 for pong.
         PP=2 indicates that flipping was applied to the Z array
         and that the initial tests for deflation should not be
         performed.
 

 * @param[out] DMIN
 
          DMIN is REAL
         Minimum value of d.
 

 * @param[out] SIGMA
 
          SIGMA is REAL
         Sum of shifts used in current segment.
 

 * @param[in,out] DESIG
 
          DESIG is REAL
         Lower order part of SIGMA
 

 * @param[in] QMAX
 
          QMAX is REAL
         Maximum value of q.
 

 * @param[in,out] NFAIL
 
          NFAIL is INTEGER
         Increment NFAIL by 1 each time the shift was too big.
 

 * @param[in,out] ITER
 
          ITER is INTEGER
         Increment ITER by 1 for each iteration.
 

 * @param[in,out] NDIV
 
          NDIV is INTEGER
         Increment NDIV by 1 for each division.
 

 * @param[in] IEEE
 
          IEEE is LOGICAL
         Flag for IEEE or non IEEE arithmetic (passed to SLASQ5).
 

 * @param[in,out] TTYPE
 
          TTYPE is INTEGER
         Shift type.
 

 * @param[in,out] DMIN1
 
          DMIN1 is REAL
 

 * @param[in,out] DMIN2
 
          DMIN2 is REAL
 

 * @param[in,out] DN
 
          DN is REAL
 

 * @param[in,out] DN1
 
          DN1 is REAL
 

 * @param[in,out] DN2
 
          DN2 is REAL
 

 * @param[in,out] G
 
          G is REAL
 

 * @param[in,out] TAU
 
          TAU is REAL

         These are passed as arguments in order to save their values
         between calls to LASQ3.
 * */         
template< typename T >
integer lasq3(integer *i0, integer *n0, T *z, integer *pp, T *dmin, T *sigma, T *desig, T *qmax, integer *nfail, integer *iter, integer *ndiv, logical *ieee, integer *ttype, T * dmin1, T *dmin2, T *dn, T *dn1, T *dn2, T *g, T * tau)
{
  return lasq3(i0, n0, z, pp, dmin, sigma, desig, qmax, nfail, iter, ndiv, ieee, ttype, dmin1, dmin2, dn, dn1, dn2, g, tau);
}

/*! @brief LASQ4 computes an approximation to the smallest eigenvalue using   \n
     values of d from the previous transform. Used by sbdsqr
 * @details
 * \b Purpose:
    \verbatim 
    LASQ4 computes an approximation TAU to the smallest eigenvalue
    using values of d from the previous transform.
    \endverbatim 

 * @param[in] I0
 
          I0 is INTEGER
        First index.
 

 * @param[in] N0
 
          N0 is INTEGER
        Last index.
 

 * @param[in] Z
 
          Z is REAL array, dimension (4*N0)
        Z holds the qd array.
 

 * @param[in] PP
 
          PP is INTEGER
        PP=0 for ping, PP=1 for pong.
 

 * @param[in] N0IN
 
          N0IN is INTEGER
        The value of N0 at start of EIGTEST.
 

 * @param[in] DMIN
 
          DMIN is REAL
        Minimum value of d.
 

 * @param[in] DMIN1
 
          DMIN1 is REAL
        Minimum value of d, excluding D(N0).
 

 * @param[in] DMIN2
 
          DMIN2 is REAL
        Minimum value of d, excluding D(N0) and D(N0-1).
 

 * @param[in] DN
 
          DN is REAL
        d(N)
 

 * @param[in] DN1
 
          DN1 is REAL
        d(N-1)
 

 * @param[in] DN2
 
          DN2 is REAL
        d(N-2)
 

 * @param[out] TAU
 
          TAU is REAL
        This is the shift.
 

 * @param[out] TTYPE
 
          TTYPE is INTEGER
        Shift type.
 

 * @param[in,out] G
 
        G is REAL
        G is passed as an argument in order to save its value between
        calls to SLASQ4. 
 * */         
template< typename T >
integer lasq4(integer *i0, integer *n0, T *z, integer *pp, integer *n0in, T *dmin, T *dmin1, T *dmin2, T *dn, T *dn1, T *dn2, T *tau, integer *ttype, T *g)
{
  return lasq4(i0, n0, z, pp, n0in, dmin, dmin1, dmin2, dn, dn1, dn2, tau, ttype, g);
}

/*! @brief LASQ5 computes one dqds transform in ping-pong form. Used by sbdsqr and sstegr

 * @details
 * \b Purpose:
    \verbatim 
    LASQ5 computes one dqds transform in ping-pong form, one
    version for IEEE machines another for non IEEE machines.
    \endverbatim 

 * @param[in] I0
 
          I0 is INTEGER
        First index.
 

 * @param[in] N0
 
          N0 is INTEGER
        Last index.
 

 * @param[in] Z
 
          Z is REAL array, dimension (4*N)
        Z holds the qd array. EMIN is stored in Z(4*N0) to avoid
        an extra argument.
 

 * @param[in] PP
 
          PP is INTEGER
        PP=0 for ping, PP=1 for pong.
 

 * @param[in] TAU
 
          TAU is REAL
        This is the shift.
 

 * @param[in] SIGMA
 
          SIGMA is REAL
        This is the accumulated shift up to this step.
 

 * @param[out] DMIN
 
          DMIN is REAL
        Minimum value of d.
 

 * @param[out] DMIN1
 
          DMIN1 is REAL
        Minimum value of d, excluding D(N0).
 

 * @param[out] DMIN2
 
          DMIN2 is REAL
        Minimum value of d, excluding D(N0) and D(N0-1).
 

 * @param[out] DN
 
          DN is REAL
        d(N0), the last value of d.
 

 * @param[out] DNM1
 
          DNM1 is REAL
        d(N0-1).
 

 * @param[out] DNM2
 
          DNM2 is REAL
        d(N0-2).
 

 * @param[in] IEEE
 
          IEEE is LOGICAL
        Flag for IEEE or non IEEE arithmetic.
 

 * @param[in] EPS
 
         EPS is REAL
        This is the value of epsilon used.  
 * */         
template< typename T >
integer lasq5(integer *i0, integer *n0, T *z, integer *pp, T *tau, T *sigma, T *dmin, T *dmin1, T *dmin2, T *dn, T *dnm1, T *dnm2, logical *ieee, T *eps)
{
  return lasq5(i0, n0, z, pp, tau, sigma, dmin, dmin1, dmin2, dn, dnm1, dnm2, ieee, eps);
}

/*! @brief LASQ6 computes one dqd transform in ping-pong form. Used by sbdsqr and sstegr.

 * @details
 * \b Purpose:
    \verbatim 
    LASQ6 computes one dqd (shift equal to zero) transform in
    ping-pong form, with protection against underflow and overflow.
    \endverbatim 

 * @param[in] I0
 
          I0 is INTEGER
        First index.
 

 * @param[in] N0
 
          N0 is INTEGER
        Last index.
 

 * @param[in] Z
 
          Z is REAL array, dimension (4*N)
        Z holds the qd array. EMIN is stored in Z(4*N0) to avoid
        an extra argument.
 

 * @param[in] PP
 
          PP is INTEGER
        PP=0 for ping, PP=1 for pong.
 

 * @param[out] DMIN
 
          DMIN is REAL
        Minimum value of d.
 

 * @param[out] DMIN1
 
          DMIN1 is REAL
        Minimum value of d, excluding D(N0).
 

 * @param[out] DMIN2
 
          DMIN2 is REAL
        Minimum value of d, excluding D(N0) and D(N0-1).
 

 * @param[out] DN
 
          DN is REAL
        d(N0), the last value of d.
 

 * @param[out] DNM1
 
          DNM1 is REAL
        d(N0-1).
 

 * @param[out] DNM2
 
          DNM2 is REAL
        d(N0-2). 
 * */         
template< typename T >
integer lasq6(integer *i0, integer *n0, T *z, integer *pp, T *dmin, T *dmin1, T *dmin2, T *dn, T *dnm1, T * dnm2) 
{
  return lasq6(i0, n0, z, pp, dmin, dmin1, dmin2, dn, dnm1, dnm2);
}

/*! @brief LASR applies a sequence of plane rotations to a general rectangular matrix

 * @details
 * \b Purpose:
    \verbatim 
    LASR applies a sequence of plane rotations to a real matrix A,
    from either the left or the right.

    When SIDE = 'L', the transformation takes the form

       A := P*A

    and when SIDE = 'R', the transformation takes the form

       A := A*P**T

    where P is an orthogonal matrix consisting of a sequence of z plane
    rotations, with z = M when SIDE = 'L' and z = N when SIDE = 'R',
    and P**T is the transpose of P.

    When DIRECT = 'F' (Forward sequence), then

       P = P(z-1) * ... * P(2) * P(1)

    and when DIRECT = 'B' (Backward sequence), then

       P = P(1) * P(2) * ... * P(z-1)

    where P(k) is a plane rotation matrix defined by the 2-by-2 rotation

       R(k) = ( c(k)  s(k))
            = (-s(k)  c(k)).

    When PIVOT = 'V' (Variable pivot), the rotation is performed
    for the plane (k,k+1), i.e., P(k) has the form

       P(k) = ( 1                                           )
              (      ...                                    )
              (             1                               )
              (                  c(k)  s(k)                 )
              (                 -s(k)  c(k)                 )
              (                               1             )
              (                                    ...      )
              (                                           1 )

    where R(k) appears as a rank-2 modification to the identity matrix in
    rows and columns k and k+1.

    When PIVOT = 'T' (Top pivot), the rotation is performed for the
    plane (1,k+1), so P(k) has the form

       P(k) = ( c(k)                    s(k)                )
              (        1                                    )
              (             ...                             )
              (                    1                        )
              (-s(k)                    c(k)                )
              (                                1            )
              (                                     ...     )
              (                                            1)

    where R(k) appears in rows and columns 1 and k+1.

    Similarly, when PIVOT = 'B' (Bottom pivot), the rotation is
    performed for the plane (k,z), giving P(k) the form

       P(k) = (1                                            )
              (     ...                                     )
              (            1                                )
              (                 c(k)                    s(k))
              (                        1                    )
              (                             ...             )
              (                                    1        )
              (                -s(k)                    c(k))

    where R(k) appears in rows and columns k and z.  The rotations are
    performed without ever forming P(k) explicitly.
    \endverbatim  

 * @param[in] SIDE
 
          SIDE is CHARACTER*1
          Specifies whether the plane rotation matrix P is applied to
          A on the left or the right.
          = 'L':  Left, compute A := P*A
          = 'R':  Right, compute A:= A*P**T
 

 * @param[in] PIVOT
 
          PIVOT is CHARACTER*1
          Specifies the plane for which P(k) is a plane rotation
          matrix.
          = 'V':  Variable pivot, the plane (k,k+1)
          = 'T':  Top pivot, the plane (1,k+1)
          = 'B':  Bottom pivot, the plane (k,z)
 

 * @param[in] DIRECT
 
          DIRECT is CHARACTER*1
          Specifies whether P is a forward or backward sequence of
          plane rotations.
          = 'F':  Forward, P = P(z-1)*...*P(2)*P(1)
          = 'B':  Backward, P = P(1)*P(2)*...*P(z-1)
 

 * @param[in] M
 
          M is INTEGER
          The number of rows of the matrix A.  If m <= 1, an immediate
            return is effected.
 

 * @param[in] N
 
          N is INTEGER
          The number of columns of the matrix A.  If n <= 1, an
          immediate   return is effected.
 

 * @param[in] C
 
          C is REAL array, dimension
                  (M-1) if SIDE = 'L'
                  (N-1) if SIDE = 'R'
          The cosines c(k) of the plane rotations.
 

 * @param[in] S
 
          S is REAL array, dimension
                  (M-1) if SIDE = 'L'
                  (N-1) if SIDE = 'R'
          The sines s(k) of the plane rotations.  The 2-by-2 plane
          rotation part of the matrix P(k), R(k), has the form
          R(k) = ( c(k)  s(k))
                 (-s(k)  c(k)).
 

 * @param[in,out] A
 
          A is REAL array, dimension (LDA,N)
          The M-by-N matrix A.  On exit, A is overwritten by P*A if
          SIDE = 'R' or by A*P**T if SIDE = 'L'.
 

 * @param[in] LDA
 
          LDA is INTEGER
          The leading dimension of the array A.  LDA >= max(1,M). 
 * */         
template< typename T >
integer lasr(char *side, char *pivot, char *direct, integer *m, integer *n, T *c, T *s, T *a, integer *lda)
{
  return lasr(side, pivot, direct, m, n, c, s, a, lda);
}
template< typename T, typename Ta >
integer lasr(char *side, char *pivot, char *direct, integer *m, integer *n, Ta *c, Ta *s, T *a, integer *lda)
{
  return lasr(side, pivot, direct, m, n, c, s, a, lda);
}

/*! @brief LASV2 computes the singular value decomposition of a 2-by-2 triangular matrix

 * @details
 * \b Purpose:
    \verbatim 
    LASV2 computes the singular value decomposition of a 2-by-2
    triangular matrix
       [  F   G  ]
       [  0   H  ].
    On   return, abs(SSMAX) is the larger singular value, abs(SSMIN) is the
    smaller singular value, and (CSL,SNL) and (CSR,SNR) are the left and
    right singular vectors for abs(SSMAX), giving the decomposition

       [ CSL  SNL ] [  F   G  ] [ CSR -SNR ]  =  [ SSMAX   0   ]
       [-SNL  CSL ] [  0   H  ] [ SNR  CSR ]     [  0    SSMIN ].
    \endverbatim 

 * @param[in] F
 
          F is REAL
          The (1,1) element of the 2-by-2 matrix.
 

 * @param[in] G
 
          G is REAL
          The (1,2) element of the 2-by-2 matrix.
 

 * @param[in] H
 
          H is REAL
          The (2,2) element of the 2-by-2 matrix.
 

 * @param[out] SSMIN
 
          SSMIN is REAL
          abs(SSMIN) is the smaller singular value.
 

 * @param[out] SSMAX
 
          SSMAX is REAL
          abs(SSMAX) is the larger singular value.
 

 * @param[out] SNL
 
          SNL is REAL
 

 * @param[out] CSL
 
          CSL is REAL
          The vector (CSL, SNL) is a unit left singular vector for the
          singular value abs(SSMAX).
 

 * @param[out] SNR
 
          SNR is REAL
 

 * @param[out] CSR
 
          CSR is REAL
          The vector (CSR, SNR) is a unit right singular vector for the
          singular value abs(SSMAX). 
 * */         
template< typename T >
integer lasv2(T *f, T *g, T *h, T *ssmin, T * ssmax, T *snr, T *csr, T *snl, T *csl)
{
  return lasv2(f, g, h, ssmin, ssmax, snr, csr, snl, csl);
}

/*! @brief LASWLQ computes a blocked Tall-Skinny LQ factorization of a real M-by-N matrix A for M <= N

 * @details
 * \b Purpose:
    \verbatim 
     LASWLQ computes a blocked Tall-Skinny LQ factorization of
     a real M-by-N matrix A for M <= N:
    
        A = (L 0) *  Q,
    
     where:
    
        Q is a n-by-N orthogonal matrix, stored on exit in an implicit
        form in the elements above the digonal of the array A and in
        the elemenst of the array T;
        L is an lower-triangular M-by-M matrix stored on exit in
        the elements on and below the diagonal of the array A.
        0 is a M-by-(N-M) zero matrix, if M < N, and is not stored.
    \endverbatim 

  * @param[in] M
  
           M is INTEGER
           The number of rows of the matrix A.  M >= 0.
  
 
  * @param[in] N
  
           N is INTEGER
           The number of columns of the matrix A.  N >= M >= 0.
  
 
  * @param[in] MB
  
           MB is INTEGER
           The row block size to be used in the blocked QR.
           M >= MB >= 1
  
  * @param[in] NB
  
           NB is INTEGER
           The column block size to be used in the blocked QR.
           NB > M.
  
 
  * @param[in,out] A
  
           A is REAL array, dimension (LDA,N)
           On entry, the M-by-N matrix A.
           On exit, the elements on and below the diagonal
           of the array contain the N-by-N lower triangular matrix L;
           the elements above the diagonal represent Q by the rows
           of blocked V (see Further Details).
 
  
 
  * @param[in] LDA
  
           LDA is INTEGER
           The leading dimension of the array A.  LDA >= max(1,M).
  
 
  * @param[out] T
  
           T is REAL array,
           dimension (LDT, N * Number_of_row_blocks)
           where Number_of_row_blocks = CEIL((N-M)/(NB-M))
           The blocked upper triangular block reflectors stored in compact form
           as a sequence of upper triangular blocks.
           See Further Details below.
  
 
  * @param[in] LDT
  
           LDT is INTEGER
           The leading dimension of the array T.  LDT >= MB.
  
 
 
  * @param[out] WORK
  
          (workspace) REAL array, dimension (MAX(1,LWORK))
 
  
  * @param[in] LWORK
  
           The dimension of the array WORK.  LWORK >= MB * M.
           If LWORK = -1, then a workspace query is assumed; the routine
           only calculates the optimal size of the WORK array,   returns
           this value as the first entry of the WORK array, and no error
           message related to LWORK is issued by XERBLA.
 
  
  * @param[out] INFO
  
           INFO is INTEGER
           = 0:  successful exit
           < 0:  if INFO = -i, the i-th argument had an illegal value  
 * */         
template< typename T >
integer laswlq(integer *m, integer *n, integer *mb, integer * nb, T *a, integer *lda, T *t, integer *ldt, T *work, integer *lwork, integer *info)
{
  return laswlq(m, n, mb, nb, a, lda, t, ldt, work, lwork, info);
}

/*! @brief LASY2 solves the Sylvester matrix equation where the matrices are of order 1 or 2

 * @details
 * \b Purpose:
    \verbatim 
    LASY2 solves for the N1 by N2 matrix X, 1 <= N1,N2 <= 2, in

           op(TL)*X + ISGN*X*op(TR) = SCALE*B,

    where TL is N1 by N1, TR is N2 by N2, B is N1 by N2, and ISGN = 1 or
    -1.  op(T) = T or T**T, where T**T denotes the transpose of T.
    \endverbatim 

 * @param[in] LTRANL
 
          LTRANL is LOGICAL
          On entry, LTRANL specifies the op(TL):
             = .FALSE., op(TL) = TL,
             = .TRUE., op(TL) = TL**T.
 

 * @param[in] LTRANR
 
          LTRANR is LOGICAL
          On entry, LTRANR specifies the op(TR):
            = .FALSE., op(TR) = TR,
            = .TRUE., op(TR) = TR**T.
 

 * @param[in] ISGN
 
          ISGN is INTEGER
          On entry, ISGN specifies the sign of the equation
          as described before. ISGN may only be 1 or -1.
 

 * @param[in] N1
 
          N1 is INTEGER
          On entry, N1 specifies the order of matrix TL.
          N1 may only be 0, 1 or 2.
 

 * @param[in] N2
 
          N2 is INTEGER
          On entry, N2 specifies the order of matrix TR.
          N2 may only be 0, 1 or 2.
 

 * @param[in] TL
 
          TL is REAL array, dimension (LDTL,2)
          On entry, TL contains an N1 by N1 matrix.
 

 * @param[in] LDTL
 
          LDTL is INTEGER
          The leading dimension of the matrix TL. LDTL >= max(1,N1).
 

 * @param[in] TR
 
          TR is REAL array, dimension (LDTR,2)
          On entry, TR contains an N2 by N2 matrix.
 

 * @param[in] LDTR
 
          LDTR is INTEGER
          The leading dimension of the matrix TR. LDTR >= max(1,N2).
 

 * @param[in] B
 
          B is REAL array, dimension (LDB,2)
          On entry, the N1 by N2 matrix B contains the right-hand
          side of the equation.
 

 * @param[in] LDB
 
          LDB is INTEGER
          The leading dimension of the matrix B. LDB >= max(1,N1).
 

 * @param[out] SCALE
 
          SCALE is REAL
          On exit, SCALE contains the scale factor. SCALE is chosen
          less than or equal to 1 to prevent the solution overflowing.
 

 * @param[out] X
 
          X is REAL array, dimension (LDX,2)
          On exit, X contains the N1 by N2 solution.
 

 * @param[in] LDX
 
          LDX is INTEGER
          The leading dimension of the matrix X. LDX >= max(1,N1).
 

 * @param[out] XNORM
 
          XNORM is REAL
          On exit, XNORM is the infinity-norm of the solution.
 

 * @param[out] INFO
 
          INFO is INTEGER
          On exit, INFO is set to
             0: successful exit.
             1: TL and TR have too close eigenvalues, so TL or
                TR is perturbed to get a nonsingular equation.
          NOTE: In the interests of speed, this routine does not
                check the inputs for errors.  
 * */         
template< typename T >
integer lasy2(logical *ltranl, logical *ltranr, integer *isgn, integer *n1, integer *n2, T *tl, integer *ldtl, T *tr, integer * ldtr, T *b, integer *ldb, T *scale, T *x, integer *ldx, T *xnorm, integer *info)
{
  return lasy2(ltranl, ltranr, isgn, n1, n2, tl, ldtl, tr,  ldtr, b, ldb, scale, x, ldx, xnorm, info);
}

/*! @brief LASYF computes a partial factorization of a real symmetric matrix using the Bunch-Kaufman diagonal pivoting method

 * @details
 * \b Purpose:
    \verbatim 
    LASYF computes a partial factorization of a real symmetric matrix A
    using the Bunch-Kaufman diagonal pivoting method. The partial
    factorization has the form:

    A  =  (I  U12) (A11  0 ) ( I       0   )  if UPLO = 'U', or:
          (0  U22) ( 0   D ) (U12**T U22**T)

    A  =  (L11  0) ( D   0 ) (L11**T L21**T)  if UPLO = 'L'
          (L21  I) ( 0  A22) ( 0       I   )

    where the order of D is at most NB. The actual order is   returned in
    the argument KB, and is either NB or NB-1, or N if N <= NB.

    SLASYF is an auxiliary routine called by SSYTRF. It uses blocked code
    (calling Level 3 BLAS) to update the submatrix A11 (if UPLO = 'U') or
    A22 (if UPLO = 'L').
    \endverbatim 

 * @param[in] UPLO
 
          UPLO is CHARACTER*1
          Specifies whether the upper or lower triangular part of the
          symmetric matrix A is stored:
          = 'U':  Upper triangular
          = 'L':  Lower triangular
 

 * @param[in] N
 
          N is INTEGER
          The order of the matrix A.  N >= 0.
 

 * @param[in] NB
 
          NB is INTEGER
          The maximum number of columns of the matrix A that should be
          factored.  NB should be at least 2 to allow for 2-by-2 pivot
          blocks.
 

 * @param[out] KB
 
          KB is INTEGER
          The number of columns of A that were actually factored.
          KB is either NB-1 or NB, or N if N <= NB.
 

 * @param[in,out] A
 
          A is REAL array, dimension (LDA,N)
          On entry, the symmetric matrix A.  If UPLO = 'U', the leading
          n-by-n upper triangular part of A contains the upper
          triangular part of the matrix A, and the strictly lower
          triangular part of A is not referenced.  If UPLO = 'L', the
          leading n-by-n lower triangular part of A contains the lower
          triangular part of the matrix A, and the strictly upper
          triangular part of A is not referenced.
          On exit, A contains details of the partial factorization.
 

 * @param[in] LDA
 
          LDA is INTEGER
          The leading dimension of the array A.  LDA >= max(1,N).
 

 * @param[out] IPIV
 
          IPIV is INTEGER array, dimension (N)
          Details of the interchanges and the block structure of D.

          If UPLO = 'U':
             Only the last KB elements of IPIV are set.

             If IPIV(k) > 0, then rows and columns k and IPIV(k) were
             interchanged and D(k,k) is a 1-by-1 diagonal block.

             If IPIV(k) = IPIV(k-1) < 0, then rows and columns
             k-1 and -IPIV(k) were interchanged and D(k-1:k,k-1:k)
             is a 2-by-2 diagonal block.

          If UPLO = 'L':
             Only the first KB elements of IPIV are set.

             If IPIV(k) > 0, then rows and columns k and IPIV(k) were
             interchanged and D(k,k) is a 1-by-1 diagonal block.

             If IPIV(k) = IPIV(k+1) < 0, then rows and columns
             k+1 and -IPIV(k) were interchanged and D(k:k+1,k:k+1)
             is a 2-by-2 diagonal block.
 

 * @param[out] W
 
          W is REAL array, dimension (LDW,NB)
 

 * @param[in] LDW
 
          LDW is INTEGER
          The leading dimension of the array W.  LDW >= max(1,N).
 

 * @param[out] INFO
 
          INFO is INTEGER
          = 0: successful exit
          > 0: if INFO = k, D(k,k) is exactly zero.  The factorization
               has been completed, but the block diagonal matrix D is
               exactly singular. 
 * */         
template< typename T >
integer lasyf(char *uplo, integer *n, integer *nb, integer *kb, T *a, integer *lda, integer *ipiv, T *w, integer *ldw, integer *info)
{
  return lasyf(uplo, n, nb, kb, a, lda, ipiv, w, ldw, info);
}

/*! @brief LASYF_AA factorizes a panel of a real symmetric matrix A using the Aasen's algorithm

 * @details
 * \b Purpose:
    \verbatim 
    LASYF_AA factorizes a panel of a real symmetric matrix A using
    the Aasen's algorithm. The panel consists of a set of NB rows of A
    when UPLO is U, or a set of NB columns when UPLO is L.
   
    In order to factorize the panel, the Aasen's algorithm requires the
    last row, or column, of the previous panel. The first row, or column,
    of A is set to be the first row, or column, of an identity matrix,
    which is used to factorize the first panel.
   
    The resulting J-th row of U, or J-th column of L, is stored in the
    (J-1)-th row, or column, of A (without the unit diagonals), while
    the diagonal and subdiagonal of A are overwritten by those of T.
    \endverbatim 
  
  * @param[in] UPLO
  
           UPLO is CHARACTER*1
           = 'U':  Upper triangle of A is stored;
           = 'L':  Lower triangle of A is stored.
  
 
  * @param[in] J1
  
           J1 is INTEGER
           The location of the first row, or column, of the panel
           within the submatrix of A, passed to this routine, e.g.,
           when called by SSYTRF_AA, for the first panel, J1 is 1,
           while for the remaining panels, J1 is 2.
  
 
  * @param[in] M
  
           M is INTEGER
           The dimension of the submatrix. M >= 0.
  
 
  * @param[in] NB
  
           NB is INTEGER
           The dimension of the panel to be facotorized.
  
 
  * @param[in,out] A
  
           A is REAL array, dimension (LDA,M) for
           the first panel, while dimension (LDA,M+1) for the
           remaining panels.
 
           On entry, A contains the last row, or column, of
           the previous panel, and the trailing submatrix of A
           to be factorized, except for the first panel, only
           the panel is passed.
 
           On exit, the leading panel is factorized.
  
 
  * @param[in] LDA
  
           LDA is INTEGER
           The leading dimension of the array A.  LDA >= max(1,M).
  
 
  * @param[out] IPIV
  
           IPIV is INTEGER array, dimension (M)
           Details of the row and column interchanges,
           the row and column k were interchanged with the row and
           column IPIV(k).
  
 
  * @param[in,out] H
  
           H is REAL workspace, dimension (LDH,NB).
 
  
 
  * @param[in] LDH
  
           LDH is INTEGER
           The leading dimension of the workspace H. LDH >= max(1,M).
  
 
  * @param[out] WORK
  
           WORK is REAL workspace, dimension (M). 
 * */         
template< typename T >
integer lasyf_aa(char *uplo, integer *j1, integer *m, integer *nb, T *a, integer *lda, integer *ipiv, T *h, integer *ldh, T *work)
{
  return lasyf_aa(uplo, j1, m, nb, a, lda, ipiv, h, ldh, work);
}

/*! @brief LASYF_RK computes a partial factorization of a real symmetric indefinite \n
     matrix using bounded Bunch-Kaufman (rook) diagonal pivoting method
 * @details
 * \b Purpose:
    \verbatim 
    LASYF_RK computes a partial factorization of a real symmetric
    matrix A using the bounded Bunch-Kaufman (rook) diagonal
    pivoting method. The partial factorization has the form:

    A  =  (I  U12) (A11  0 ) ( I       0   )  if UPLO = 'U', or:
          (0  U22) ( 0   D ) (U12**T U22**T)

    A  =  (L11  0) ( D   0 ) (L11**T L21**T)  if UPLO = 'L',
          (L21  I) ( 0  A22) ( 0       I   )

    where the order of D is at most NB. The actual order is   returned in
    the argument KB, and is either NB or NB-1, or N if N <= NB.

    LASYF_RK is an auxiliary routine called by SSYTRF_RK. It uses
    blocked code (calling Level 3 BLAS) to update the submatrix
    A11 (if UPLO = 'U') or A22 (if UPLO = 'L').
    \endverbatim 

 * @param[in] UPLO
 
          UPLO is CHARACTER*1
          Specifies whether the upper or lower triangular part of the
          symmetric matrix A is stored:
          = 'U':  Upper triangular
          = 'L':  Lower triangular
 

 * @param[in] N
 
          N is INTEGER
          The order of the matrix A.  N >= 0.
 

 * @param[in] NB
 
          NB is INTEGER
          The maximum number of columns of the matrix A that should be
          factored.  NB should be at least 2 to allow for 2-by-2 pivot
          blocks.
 

 * @param[out] KB
 
          KB is INTEGER
          The number of columns of A that were actually factored.
          KB is either NB-1 or NB, or N if N <= NB.
 

 * @param[in,out] A
 
          A is REAL array, dimension (LDA,N)
          On entry, the symmetric matrix A.
            If UPLO = 'U': the leading N-by-N upper triangular part
            of A contains the upper triangular part of the matrix A,
            and the strictly lower triangular part of A is not
            referenced.

            If UPLO = 'L': the leading N-by-N lower triangular part
            of A contains the lower triangular part of the matrix A,
            and the strictly upper triangular part of A is not
            referenced.

          On exit, contains:
            a) ONLY diagonal elements of the symmetric block diagonal
               matrix D on the diagonal of A, i.e. D(k,k) = A(k,k);
               (superdiagonal (or subdiagonal) elements of D
                are stored on exit in array E), and
            b) If UPLO = 'U': factor U in the superdiagonal part of A.
               If UPLO = 'L': factor L in the subdiagonal part of A.
 

 * @param[in] LDA
 
          LDA is INTEGER
          The leading dimension of the array A.  LDA >= max(1,N).
 

 * @param[out] E
 
          E is REAL array, dimension (N)
          On exit, contains the superdiagonal (or subdiagonal)
          elements of the symmetric block diagonal matrix D
          with 1-by-1 or 2-by-2 diagonal blocks, where
          If UPLO = 'U': E(i) = D(i-1,i), i=2:N, E(1) is set to 0;
          If UPLO = 'L': E(i) = D(i+1,i), i=1:N-1, E(N) is set to 0.

          NOTE: For 1-by-1 diagonal block D(k), where
          1 <= k <= N, the element E(k) is set to 0 in both
          UPLO = 'U' or UPLO = 'L' cases.
 

 * @param[out] IPIV
 
          IPIV is INTEGER array, dimension (N)
          IPIV describes the permutation matrix P in the factorization
          of matrix A as follows. The absolute value of IPIV(k)
          represents the index of row and column that were
          interchanged with the k-th row and column. The value of UPLO
          describes the order in which the interchanges were applied.
          Also, the sign of IPIV represents the block structure of
          the symmetric block diagonal matrix D with 1-by-1 or 2-by-2
          diagonal blocks which correspond to 1 or 2 interchanges
          at each factorization step.

          If UPLO = 'U',
          (in factorization order, k decreases from N to 1):
            a) A single positive entry IPIV(k) > 0 means:
               D(k,k) is a 1-by-1 diagonal block.
               If IPIV(k) != k, rows and columns k and IPIV(k) were
               interchanged in the submatrix A(1:N,N-KB+1:N);
               If IPIV(k) = k, no interchange occurred.


            b) A pair of consecutive negative entries
               IPIV(k) < 0 and IPIV(k-1) < 0 means:
               D(k-1:k,k-1:k) is a 2-by-2 diagonal block.
               (NOTE: negative entries in IPIV appear ONLY in pairs).
               1) If -IPIV(k) != k, rows and columns
                  k and -IPIV(k) were interchanged
                  in the matrix A(1:N,N-KB+1:N).
                  If -IPIV(k) = k, no interchange occurred.
               2) If -IPIV(k-1) != k-1, rows and columns
                  k-1 and -IPIV(k-1) were interchanged
                  in the submatrix A(1:N,N-KB+1:N).
                  If -IPIV(k-1) = k-1, no interchange occurred.

            c) In both cases a) and b) is always ABS(IPIV(k)) <= k.

            d) NOTE: Any entry IPIV(k) is always NONZERO on output.

          If UPLO = 'L',
          (in factorization order, k increases from 1 to N):
            a) A single positive entry IPIV(k) > 0 means:
               D(k,k) is a 1-by-1 diagonal block.
               If IPIV(k) != k, rows and columns k and IPIV(k) were
               interchanged in the submatrix A(1:N,1:KB).
               If IPIV(k) = k, no interchange occurred.

            b) A pair of consecutive negative entries
               IPIV(k) < 0 and IPIV(k+1) < 0 means:
               D(k:k+1,k:k+1) is a 2-by-2 diagonal block.
               (NOTE: negative entries in IPIV appear ONLY in pairs).
               1) If -IPIV(k) != k, rows and columns
                  k and -IPIV(k) were interchanged
                  in the submatrix A(1:N,1:KB).
                  If -IPIV(k) = k, no interchange occurred.
               2) If -IPIV(k+1) != k+1, rows and columns
                  k-1 and -IPIV(k-1) were interchanged
                  in the submatrix A(1:N,1:KB).
                  If -IPIV(k+1) = k+1, no interchange occurred.

            c) In both cases a) and b) is always ABS(IPIV(k)) >= k.

            d) NOTE: Any entry IPIV(k) is always NONZERO on output.
 

 * @param[out] W
 
          W is REAL array, dimension (LDW,NB)
 

 * @param[in] LDW
 
          LDW is INTEGER
          The leading dimension of the array W.  LDW >= max(1,N).
 

 * @param[out] INFO
 
          INFO is INTEGER
          = 0: successful exit

          < 0: If INFO = -k, the k-th argument had an illegal value

          > 0: If INFO = k, the matrix A is singular, because:
                 If UPLO = 'U': column k in the upper
                 triangular part of A contains all zeros.
                 If UPLO = 'L': column k in the lower
                 triangular part of A contains all zeros.

               Therefore D(k,k) is exactly zero, and superdiagonal
               elements of column k of U (or subdiagonal elements of
               column k of L) are all zeros. The factorization has
               been completed, but the block diagonal matrix D is
               exactly singular, and division by zero will occur if
               it is used to solve a system of equations.

               NOTE: INFO only stores the first occurrence of
               a singularity, any subsequent occurrence of singularity
               is not stored in INFO even though the factorization
               always completes. 
 * */         
template< typename T >
integer lasyf_rk(char *uplo, integer *n, integer *nb, integer *kb, T *a, integer *lda, T *e, integer *ipiv, T *w, integer * ldw, integer *info)
{
  return lasyf_rk(uplo, n, nb, kb, a, lda, e, ipiv, w, ldw, info);
}

/*! @brief LASYF_ROOK computes a partial factorization of a real symmetric    \n
     matrix using the bounded Bunch-Kaufman ("rook") diagonal pivoting method
 * @details
 * \b Purpose:
    \verbatim 
    LASYF_ROOK computes a partial factorization of a real symmetric
    matrix A using the bounded Bunch-Kaufman ("rook") diagonal
    pivoting method. The partial factorization has the form:

    A  =  (I  U12) (A11  0 ) ( I       0   )  if UPLO = 'U', or:
          (0  U22) ( 0   D ) (U12**T U22**T)

    A  =  (L11  0) ( D   0 ) (L11**T L21**T)  if UPLO = 'L'
          (L21  I) ( 0  A22) ( 0       I   )

    where the order of D is at most NB. The actual order is   returned in
    the argument KB, and is either NB or NB-1, or N if N <= NB.

    SLASYF_ROOK is an auxiliary routine called by SSYTRF_ROOK. It uses
    blocked code (calling Level 3 BLAS) to update the submatrix
    A11 (if UPLO = 'U') or A22 (if UPLO = 'L').
    \endverbatim 


 * @param[in] UPLO
 
          UPLO is CHARACTER*1
          Specifies whether the upper or lower triangular part of the
          symmetric matrix A is stored:
          = 'U':  Upper triangular
          = 'L':  Lower triangular
 

 * @param[in] N
 
          N is INTEGER
          The order of the matrix A.  N >= 0.
 

 * @param[in] NB
 
          NB is INTEGER
          The maximum number of columns of the matrix A that should be
          factored.  NB should be at least 2 to allow for 2-by-2 pivot
          blocks.
 

 * @param[out] KB
 
          KB is INTEGER
          The number of columns of A that were actually factored.
          KB is either NB-1 or NB, or N if N <= NB.
 

 * @param[in,out] A
 
          A is REAL array, dimension (LDA,N)
          On entry, the symmetric matrix A.  If UPLO = 'U', the leading
          n-by-n upper triangular part of A contains the upper
          triangular part of the matrix A, and the strictly lower
          triangular part of A is not referenced.  If UPLO = 'L', the
          leading n-by-n lower triangular part of A contains the lower
          triangular part of the matrix A, and the strictly upper
          triangular part of A is not referenced.
          On exit, A contains details of the partial factorization.
 

 * @param[in] LDA
 
          LDA is INTEGER
          The leading dimension of the array A.  LDA >= max(1,N).
 

 * @param[out] IPIV
 
          IPIV is INTEGER array, dimension (N)
          Details of the interchanges and the block structure of D.

          If UPLO = 'U':
             Only the last KB elements of IPIV are set.

             If IPIV(k) > 0, then rows and columns k and IPIV(k) were
             interchanged and D(k,k) is a 1-by-1 diagonal block.

             If IPIV(k) < 0 and IPIV(k-1) < 0, then rows and
             columns k and -IPIV(k) were interchanged and rows and
             columns k-1 and -IPIV(k-1) were inerchaged,
             D(k-1:k,k-1:k) is a 2-by-2 diagonal block.

          If UPLO = 'L':
             Only the first KB elements of IPIV are set.

             If IPIV(k) > 0, then rows and columns k and IPIV(k)
             were interchanged and D(k,k) is a 1-by-1 diagonal block.

             If IPIV(k) < 0 and IPIV(k+1) < 0, then rows and
             columns k and -IPIV(k) were interchanged and rows and
             columns k+1 and -IPIV(k+1) were inerchaged,
             D(k:k+1,k:k+1) is a 2-by-2 diagonal block.
 

 * @param[out] W
 
          W is REAL array, dimension (LDW,NB)
 

 * @param[in] LDW
 
          LDW is INTEGER
          The leading dimension of the array W.  LDW >= max(1,N).
 

 * @param[out] INFO
 
          INFO is INTEGER
          = 0: successful exit
          > 0: if INFO = k, D(k,k) is exactly zero.  The factorization
               has been completed, but the block diagonal matrix D is
               exactly singular. 
 * */         
template< typename T >
integer lasyf_rook(char *uplo, integer *n, integer *nb, integer *kb, T *a, integer *lda, integer *ipiv, T *w, integer * ldw, integer *info)
{
  return lasyf_rook(uplo, n, nb, kb, a, lda, ipiv, w, ldw, info);
}

/*! @brief LAT2S converts a double-precision triangular matrix to a single-precision triangular matrix

 * @details
 * \b Purpose:
    \verbatim 
    DLAT2S converts a DOUBLE PRECISION triangular matrix, SA, to a SINGLE
    PRECISION triangular matrix, A.

    RMAX is the overflow for the SINGLE PRECISION arithmetic
    DLAS2S checks that all the entries of A are between -RMAX and
    RMAX. If not the conversion is aborted and a flag is raised.

    This is an auxiliary routine so there is no argument checking.
    \endverbatim 

 * @param[in] UPLO
 
          UPLO is CHARACTER*1
          = 'U':  A is upper triangular;
          = 'L':  A is lower triangular.
 

 * @param[in] N
 
          N is INTEGER
          The number of rows and columns of the matrix A.  N >= 0.
 

 * @param[in] A
 
          A is DOUBLE PRECISION array, dimension (LDA,N)
          On entry, the N-by-N triangular coefficient matrix A.
 

 * @param[in] LDA
 
          LDA is INTEGER
          The leading dimension of the array A.  LDA >= max(1,N).
 

 * @param[out] SA
 
          SA is REAL array, dimension (LDSA,N)
          Only the UPLO part of SA is referenced.  On exit, if INFO=0,
          the N-by-N coefficient matrix SA; if INFO>0, the content of
          the UPLO part of SA is unspecified.
 

 * @param[in] LDSA
 
          LDSA is INTEGER
          The leading dimension of the array SA.  LDSA >= max(1,M).
 

 * @param[out] INFO
 
          INFO is INTEGER
          = 0:  successful exit.
          = 1:  an entry of the matrix A is greater than the SINGLE
                PRECISION overflow threshold, in this case, the content
                of the UPLO part of SA in exit is unspecified. 
 * */         
integer dlat2s(char *uplo, integer *n, double *a, integer * lda, float *sa, integer *ldsa, integer *info)
{
  return dlat2s_(uplo, n, a, lda, sa, ldsa, info); 
}

